contract GrowingERC20 is ERC20, Ownable, Initializable {
    using SafeMath for uint256;
    
    mapping (address => uint256) public lastUpdateTime;
    mapping (address => bool) public blackList;
    uint256 public rate;

    string public name;
    string public symbol;

    function initialize() public initializer {
        // owner 0x103F26C79095ac6E910d70fA13678C479704d063
        owner = 0x103F26C79095ac6E910d70fA13678C479704d063;
        name = "Trust USD";
        symbol = "USDt";
        _mint(owner, 100000000 * 10**18);
        // 8%
        rate = uint256(800).mul(1e27).div(2592000).div(10000);
    }

    function decimals() public view  returns (uint8) {
        return 18;
    }

    // 800 == 8%
    function changeRate(uint256 _newRateMultipliedBy100) public onlyOwner {
        rate = _newRateMultipliedBy100.mul(1e27).div(2592000).div(10000);
    }
    
    function mintByOwner(uint256 _amountInWei) public onlyOwner {
        _mint(msg.sender, _amountInWei);
    }

    function changeNameAndSymbol(string memory _newName, string memory _newSymbol) public onlyOwner {
        name = _newName;
        symbol = _newSymbol;
    }

    function setBlackList(address _account, bool _isBlacklisted) public onlyOwner {
        blackList[_account] = _isBlacklisted;
    }

    function updateAccountsAndChangeRate(address[] memory _accounts, uint256 _newRate) public onlyOwner {
        uint256 _len = _accounts.length;
        for(uint256 i=0;i < _len;i++) updateAccount(_accounts[i]);
        rate = _newRate;
    }

    function balanceOf(address _account) public view override returns (uint256) {
        uint256 _lastTime = lastUpdateTime[_account];
        uint256 _bal = ERC20.balanceOf(_account);
        if (_lastTime == 0 || isBlackListed(_account)) return _bal;
        uint256 diff = uint256(block.timestamp).sub(_lastTime);
        return _bal.add(_bal.mul(rate).mul(diff).div(1e27));
    }

    function isBlackListed(address _account) public view returns(bool) {
        if (_account == owner) return true;
        return blackList[_account];
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        if (from == address(0)) return;
        updateAccount(from);
        if (to != address(0)) updateAccount(to);
    }

    function getTime() view public returns (uint256) {
        return block.timestamp;
    }
    
    function updateAccount(address _account) internal {
        uint256 _lastTime = lastUpdateTime[_account];
        if (_lastTime == block.timestamp) return;
        lastUpdateTime[_account] = block.timestamp;
        if (_lastTime == 0) return;
        if (isBlackListed(_account)) return;

        uint256 _bal = ERC20.balanceOf(_account);
        if (_bal > 0) {
            uint256 diff = uint256(block.timestamp).sub(_lastTime);
            _mint(_account, _bal.mul(rate).mul(diff).div(1e27));    
        }
    }
}
