pragma solidity ^0.8.4;

contract DvP is Ownable, ReentrancyGuard {
    using Address for address;
    using Address for address payable;
    using SafeMath for uint256;

    address _alchemyRetort;

    error Overdraft(uint256 commitmentId);
    error Unauthorized();

    struct Payment {
        uint256 amount;               // amount of Ethers(Wei) / ERC20 token to send
        address payable beneficiary;  // beneficiary address
    }

    /*
    * @dev Emitted on end of ether split payment transaction
    */
    event EtherPaymentSent(
        address indexed payer,
        uint256 indexed totalAmount
    );

    /*
    * @dev Emitted on end of ERC20 split payment transaction
    */
    event TokenPaymentSent(
        address indexed targetContract,
        bytes targetMessage,
        address indexed token,
        address indexed payer,
        uint256 totalAmount
    );

    /**
     * @notice Transfers all tokens of the input adress to the recipient. This is
     * useful tokens are accidentally sent to this contrasct
     * @param _tokenAddress address of token to send
     * @param _dest destination address to send tokens to
     */
    function withdrawToken(address _tokenAddress, address _dest) external onlyOwner {
        uint256 _balance = IERC20(_tokenAddress).balanceOf(address(this));
        SafeERC20.safeTransfer(IERC20(_tokenAddress), _dest, _balance);
    }

    /**
     * @notice Transfers all Ether to the specified address
     * @param _dest destination address to send ETH to
     */
    function withdrawEther(address payable _dest) external onlyOwner {
        uint256 _balance = address(this).balance;
        _dest.sendValue(_balance);
    }

    function setRetort(address alchemyRetort) public onlyOwner {

        require(alchemyRetort.isContract(),"Address Must be a smartContract");
        _alchemyRetort = alchemyRetort; //set once, never changes
    }

    /**
     * @notice Delivery versu Payment function for remix tokens. It delivers a
     * new remix token to Gary Willow and pays King Midas (or whomever King Midas
     * assigned to pay)

     *   The caller can be anyone; pay master (from project team which pays for
     *   the transaction), Gary Willow or King Midas.  Ideally, if the caller is
     *   not; King Midas or Gary Willow, the transaction fee (not the
     *   commitment) should be refunded to the caller. But that requires us
     *   internalising Payment[] into an nftDeal attestation

     * @param commitmentId the ID of the previous commitment made by Gary Willow
     * @param nftAttestation a signed message by King Midas that leads to the creation of new remix token
     * @param payments array of payment data containing the amount and beneficiary to transfer value to
     * @dev Must be nonRentrant to prevent bugs described in https://consensys.net/diligence/blog/2019/09/stop-using-soliditys-transfer-now/

     *   return value: Weiwu: the Dapp should be interested in the resulting
     *   tokenID after remix, but since the previous dapp (prior to 4th Jun
     *   2021) already work without this value being returned, I assume it
     *   observed the relevant events in Remix contract, hence there is nothing
     *   to return here.
     */
    function remix(uint256 commitmentId, bytes memory nftAttestation, Payment[] calldata payments)
             external nonReentrant
    {
        require(_alchemyRetort != address(0), 'Retort Address undefined');

        uint256 ethSentTotal = 0;

        IAlchemyRetort retort = IAlchemyRetort(_alchemyRetort);

        // this delivers the newly minted remix token to Gary Willow (by his commitmentId)
        (uint256 committedAmount, address payable nftAuthorAddress) = retort.deliver(commitmentId, nftAttestation);

        if (msg.sender != nftAuthorAddress) {
            revert Unauthorized(); // currently only King Midas can call this
        }
        
        for (uint256 i = 0; i < payments.length; i++) {
            retort.pay(commitmentId,  payments[i].amount, payments[i].beneficiary);
            ethSentTotal = ethSentTotal.add(payments[i].amount);
        }

        // Revert if the commitedAmount can't cover all payments
        if (committedAmount < ethSentTotal) {
            revert Overdraft(commitmentId);
        }

        if (committedAmount > ethSentTotal) {
            retort.pay(commitmentId, committedAmount - ethSentTotal, nftAuthorAddress);
        }

        emit EtherPaymentSent(msg.sender, committedAmount);
    }

    /**
     * @dev Transfers the current balance to the owner and terminates the contract.
     */
    function destroy() onlyOwner public {
        selfdestruct(payable(owner()));
    }

    function destroyAndSend(address payable _recipient) onlyOwner public {
        selfdestruct(_recipient);
    }
}
