pragma solidity ^0.8.0;
contract MyNFT is ERC721Upgradeable, UUPSUpgradeable, OwnableUpgradeable {
    // replaced Address lib with custom MyAddress to avoid delegateCall (its not allowed for Proxied contract)
    using AddressUtil for address;
    using Strings for uint256;

    // Token data
    string private constant _name = "My NFT";
    string private constant _symbol = "NFT";
    string private constant _JSON_FILE = ".json";
    string private constant _remixTokenURI = "https://alchemynft.io/";

    uint256 private _tokenIdIndex;

    // this function name required, Hardhat use it to call initialize() on proxy deploy step
    function initialize() public initializer {
        __Ownable_init();
        _tokenIdIndex = 1; //set once, can never be set again (contract updates this value)
    }

    // its enough to test modifier onlyOwner, other checks, like isContract() , is contain upgradeTo() method implemented by UUPSUpgradeable
    function _authorizeUpgrade(address) internal override onlyOwner {}

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function contractURI() public pure returns (string memory) {
        return "https://alchemynft.io/contracts/NFT.json";
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        return string(abi.encodePacked(_remixTokenURI, block.chainid.toString(), "/", contractAddress(), "/", tokenId.toString(), _JSON_FILE));
    }

    function mintUsingSequentialTokenId(address to) public {
        _mint(to, _tokenIdIndex);
        _tokenIdIndex++;
    }
    
    // Note: metadataUID is an unused 'breadcrumb' for tracking; we read the input params
    function mintSequential(address to, string memory metadataUID) public { 
        mintUsingSequentialTokenId(to);
    }

    function contractAddress() internal view returns (string memory) {
        return Strings.toHexString(uint160(address(this)), 20);
    }
}
