// File contracts/common/AccessControlMixinUpgradeable.sol

pragma solidity ^0.8.0;


abstract
contract AccessControlMixinUpgradeable is AccessControlEnumerableUpgradeable
{
    string private _revertMsg;

    function _setupContractId(string memory contractId) internal {
        _revertMsg = string(abi.encodePacked(contractId, ": INSUFFICIENT_PERMISSIONS"));
    }

    modifier only(bytes32 role) {
        require(
            hasRole(role, _msgSender()),
            _revertMsg
        );
        _;
    }

    function grantRoleBulk(bytes32 role, address[] memory accounts) public virtual onlyRole(getRoleAdmin(role)) {
        for (uint i = 0; i < accounts.length; i++) {
            grantRole(role, accounts[i]);
        }
    }

    function revokeRoleBulk(bytes32 role, address[] memory accounts) public virtual onlyRole(getRoleAdmin(role)) {
        for (uint i = 0; i < accounts.length; i++) {
            revokeRole(role, accounts[i]);
        }
    }

    uint256[49] private __gap;
}


// File contracts/common/ContextMixin.sol

pragma solidity ^0.8.0;

abstract
contract ContextMixin
{
    function msgSender() internal view returns (address sender) {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }
}


