// File contracts/bridge/RootBridge.sol

pragma solidity ^0.8.0;







contract RootBridge is
        AccessControlMixinUpgradeable,
        ContextMixin,
        UUPSUpgradeable,
        IRootBridge
{
    using SafeMathUpgradeable for uint256;
    using RLPReader for bytes;
    using RLPReader for RLPReader.RLPItem;

    bytes32 public constant FUNDER_ROLE = keccak256("FUNDER_ROLE");
    bytes32 public constant WITHDRAWAL_ROLE = keccak256("WITHDRAWAL_ROLE");

    address private _proofSigner;
    uint256 private _feeRate = 250;   //Fee percentage * 100; 1% = 100; 2.5% = 250; 100% = 10000; default value 2.5%
    uint256 private _collectedFees = 0;
    mapping(bytes32 => bool) public processedWithdrawals;


    event EthFunded(
        address indexed funder,
        uint256 amount
    );

    event EthDefunded(
        address indexed defunder,
        uint256 amount
    );

    event EthDepositIntitated(
        address indexed depositor,
        uint256 amount,
        uint256 feeAmount
    );

    event EthWithdrawn(
        address indexed withdrawer,
        uint256 amount
    );


    function initialize(address signer_, uint256 feeRate_) public virtual initializer {
        _setSigner(signer_);
        _setFeeRate(feeRate_);
        _setupContractId("RootBridge");
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(FUNDER_ROLE, _msgSender());
        _setupRole(WITHDRAWAL_ROLE, _msgSender());
    }

    // This is to support Native meta transactions
    // never use msg.sender directly, use _msgSender() instead
    function _msgSender() internal override view returns (address) {
        return ContextMixin.msgSender();
    }

    /**
     * @dev See {UUPSUpgradeable-_authorizeUpgrade}.
     */
    function _authorizeUpgrade(address) internal virtual override only(DEFAULT_ADMIN_ROLE) {
        this;
    }

    function getImplementation() public view returns (address) {
        return _getImplementation();
    }


    function fundEther() external payable only(FUNDER_ROLE) {
        require(_msgSender() != address(0), "Funder address cannot be the null address");
        require(msg.value > 0, "Funding amount must be greater than 0");
        emit EthFunded(_msgSender(), msg.value);
    }

    function defundEther(uint256 amount) external only(FUNDER_ROLE) {
        require(_msgSender() != address(0), "Funder address cannot be the null address");
        require(amount > 0, "Amount must be greater than 0");
        require(address(this).balance >= amount, "Insufficient balance to defund requested amount");
        payable(_msgSender()).transfer(amount);
        emit EthDefunded(_msgSender(), amount);
    }

    function fundBalance() public view returns (uint256) {
        return address(this).balance;
    }


    function setSigner(address signer_) public only(DEFAULT_ADMIN_ROLE) {
        _setSigner(signer_);
    }

    function _setSigner(address signer_) internal {
        require(signer_ != address(0), "Invalid account");
        _proofSigner = signer_;
    }

    function signer() public view returns (address) {
        return _proofSigner;
    }

    function setFeeRate(uint256 feeRate_) public only(DEFAULT_ADMIN_ROLE) {
        _setFeeRate(feeRate_);
    }

    function _setFeeRate(uint256 feeRate_) internal {
        require(feeRate_ <= 10000, "Invalid fee rate");
        _feeRate = feeRate_;
    }

    function feeRate() external view override returns (uint256) {
        return _feeRate;
    }

    function feeAmount(uint256 amount) external view override returns (uint256) {
        return _calculateFee(amount);
    }

    function _calculateFee(uint256 amount) internal view returns (uint256) {
        //TODO: Need to implement math functions that protect against overflow
        return amount.mul(_feeRate).div(10000);
    }

    function collectedFees() external view returns (uint256) {
        return _collectedFees;
    }


    function depositEtherFor(address userAddress, uint256 amount) external override payable {
        require(userAddress != address(0), "User address cannot be the null address");
        require(amount > 0, "Deposit amount must be greater than 0");
        uint256 fee = _calculateFee(amount);
        require(amount + fee == msg.value, "amount + fee does not equal msg.value");
        _collectedFees += fee;
        emit EthDepositIntitated(userAddress, amount, fee);
    }

    /**
     * @notice withdraw ether by providing proof
     * @dev This function accepts a proof from the YH oracle to authorize the final step of the withdrawal.
     * Upon verification of the proof the withdrawn ETH will be transferred to the users wallet.
     *
     * @param proof RLP encoded data of the reference tx containing following list of fields
     *  0 - txnHash -
     *  1 - userAddress -
     *  2 - amount -
     */
    function withdrawEther(bytes calldata proof, bytes32 sigR, bytes32 sigS, uint8 sigV) external override {
        require(verifyProof(proof, sigR, sigS, sigV), "Invalid proof signature");

        //extract data from the proof
        RLPReader.RLPItem[] memory proofRLPList = proof
            .toRlpItem()
            .toList();
        bytes32 txnHash = bytes32(proofRLPList[0].toUint());
        address receiverAddress = proofRLPList[1].toAddress();
        uint256 amount = proofRLPList[2].toUint();

        require(processedWithdrawals[txnHash] != true, "Withdrawal has already processed for this transaction hash");
        require(receiverAddress != address(0), "Receiver address cannot be the null address");
        require(amount > 0, "Withdrawal amount must be greater than 0");
        require(amount <= address(this).balance, "Withdrawal amount exceeds the fund balance");

        payable(receiverAddress).transfer(amount);
        processedWithdrawals[txnHash] = true;
        emit EthWithdrawn(receiverAddress, amount);
    }

    function verifyProof(bytes calldata proof, bytes32 sigR, bytes32 sigS, uint8 sigV) internal view returns (bool) {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        return _proofSigner == ecrecover(
            keccak256(abi.encodePacked(prefix, keccak256(proof))),
            sigV,
            sigR,
            sigS
        );
    }


    uint256[46] private __gap;
}
