/**
 * @dev A token holder contract that will allow a beneficiary to extract the
 * tokens after a given release time.
 *
 * Useful for simple vesting schedules like "advisors get all of their tokens
 * after 1 year".
 */
contract UsdtContract {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using Address for address;
    
    // admin
    address public admin;

    // ERC20 basic token contract being held
    IERC20 public token;

    // beneficiary of tokens after they are released
    address public beneficiary;
    
    // last paid timestamp
    uint256 public lastTimestamp;
    
    // release days
    uint256 public releaseInterval = 14;
    
    // release token amount
    uint256 public releaseAmount = 2000e6;

    // status
    bool public isStopped = false;

    /// @notice Emitted when admin is changed
    event NewAdmin(address oldAdmin, address newAdmin);

    /// @notice Emitted when token is changed
    event NewToken(address oldToken, address newToken);

    /// @notice Emitted when beneficiary is changed
    event NewBeneficiary(address oldBeneficiary, address newBeneficiary);

    /// @notice Emitted when release interval is changed
    event NewReleaseInterval(uint256 oldReleaseInterval, uint256 newReleaseInterval);

    /// @notice Emitted when release amount is changed
    event NewReleaseAmount(uint256 oldReleaseAmount, uint256 newReleaseAmount);
    
    constructor (IERC20 token_, address beneficiary_) public {
        admin = msg.sender;
        
        token = token_;
        beneficiary = beneficiary_;

        lastTimestamp = block.timestamp;
    }
    
    modifier onlyAdmin {
        require(msg.sender == admin, "only admin");
        _;
    }
    
    /*** Set Admin ***/
    function setAdmin(address newAdmin) external onlyAdmin {
        address oldAdmin = admin;
        admin = newAdmin;
        emit NewAdmin(oldAdmin, newAdmin);
    }
    
    /*** Set Token ***/
    function setToken(IERC20 newToken) external onlyAdmin {
        IERC20 oldToken = token;
        token = newToken;
        emit NewToken(address(oldToken), address(newToken));
    }
    
    /*** Set Beneficiary ***/
    function setBeneficiary(address newBeneficiary) external onlyAdmin {
        address oldBeneficiary = beneficiary;
        beneficiary = newBeneficiary;
        emit NewBeneficiary(oldBeneficiary, newBeneficiary);
    }
    
    /*** Set Release Interval ***/
    function setReleaseInterval(uint256 newReleaseInterval) external onlyAdmin {
        uint256 oldReleaseInterval = releaseInterval;
        releaseInterval = newReleaseInterval;
        emit NewReleaseInterval(oldReleaseInterval, newReleaseInterval);
    }
    
    /*** Set Release Amount ***/
    function setReleaseAmount(uint256 newReleaseAmount) external onlyAdmin {
        uint256 oldReleaseAmount = releaseAmount;
        releaseAmount = newReleaseAmount;
        emit NewReleaseAmount(oldReleaseAmount, newReleaseAmount);
    }

    /**
     * @notice Transfers tokens held by contract to beneficiary.
     */
    function release() public {
        require(!isStopped, "contract is stopped");
        uint256 curTimestamp = getBlockTimestamp();
        require(curTimestamp >= lastTimestamp + releaseInterval * 1 days, "can't release");

        uint256 amount = token.balanceOf(address(this));
        require(amount > 0, "no tokens to release");

        uint256 actualAmount;
        if (amount >= releaseAmount) {
            actualAmount = releaseAmount;
        } else {
            actualAmount = amount;
        }
        
        lastTimestamp = lastTimestamp + releaseInterval * 1 days;
        
        token.safeTransfer(beneficiary, actualAmount);
    }

    /**
     * @return the release amount.
     */
    function lockedBalance() public view returns (uint256) {
        return token.balanceOf(address(this));
    }

    /**
     * @return the block timestamp.
     */
    function getBlockTimestamp() public view returns (uint) {
        return block.timestamp;
    }

    /**
     * @notice can transfer out any accidentally sent ERC20 tokens
     */
    function transferAnyERC20Token(address tokenAddress, uint tokens) public onlyAdmin {
        return IERC20(tokenAddress).safeTransfer(admin, tokens);
    }

    /**
     * @notice start
     */
    function start() public onlyAdmin {
        isStopped = false;
        lastTimestamp = getBlockTimestamp();
    }

    /**
     * @notice reset timestamp
     * 
     */
    function reset() public onlyAdmin {
        lastTimestamp = getBlockTimestamp();
    }

    /**
     * @notice stop salary status
     */
    function stop() public onlyAdmin {
        isStopped = true;
    }

    /**
     * @notice Does not accept ETH.
     */
    fallback() external payable {
        revert();
    }

    /**
     * @notice Does not accept ETH.
     */
    receive() external payable {
        revert();
    }
}
