        address[] calldata _burners,
        uint256[] calldata _ids,
        bool[] calldata _statuses
    ) external onlyOwner {
        require(_burners.length == _statuses.length, "Array length mismatch");
        require(_burners.length > 0, "Array length must be > 0");
        require(!_burners.hasDuplicate(), "Cannot duplicate burners");

        for (uint256 i = 0; i < _burners.length; i++) {
