     *
     * @param _contractId Id of TLC to return from.
     * @return bool true on success
     */
    function withdraw(bytes32 _contractId)
        external
        contractExists(_contractId)
        withdrawable(_contractId)
        returns (bool)
    {
        LockContract storage c = contracts[_contractId];
        c.withdrawn = true;
        ERC721(c.tokenContract).transferFrom(
            address(this),
            c.sender,
            c.tokenId
        );
        emit TLCERC721Withdraw(_contractId);
        return true;
    }

    /**
     * @dev Get contract details.
     * @param _contractId TLC contract id
     */
    function getContract(bytes32 _contractId)
        public
        view
        returns (
            address sender,
            address tokenContract,
            uint256 tokenId,
            uint256 timelock,
            bool withdrawn
        )
    {
        if (haveContract(_contractId) == false)
            return (address(0), address(0), 0, 0, false);
        LockContract memory c = contracts[_contractId];
        return (c.sender, c.tokenContract, c.tokenId, c.timelock, c.withdrawn);
    }

    /**
     * @dev Is there a contract with id _contractId.
     * @param _contractId Id into contracts mapping.
     */
    function haveContract(bytes32 _contractId)
        internal
        view
        returns (bool exists)
    {
        exists = (contracts[_contractId].sender != address(0));
    }
}
