contract Privex is ERC20, Ownable{
    
    mapping(address => bool) addressExist;

    uint private taxation = 6; 

    bool private firstInit = true;
    
    address private _ownerAddress;
    address private _charityWallet;
    address private _marketingWallet;
    address private _burnWallet;
    address[] internal _staking;
    function addStakeholder(address _walletAddress) public onlyOwner{
        require(!addressExist[_walletAddress], "This addres already in system");
        _staking.push(_walletAddress);
        addressExist[_walletAddress] = true;
    }
    
    function setCharityWallet(address _walletAddress) private returns(bool){
        _charityWallet = _walletAddress;
        return true;
    }
    
    function setMarketingWallet(address _walletAddress) public returns(bool){
        _marketingWallet = _walletAddress;
        return true;
    }
    
    function removeStakeholder(address _stakeholder)public onlyOwner{
        require(addressExist[_stakeholder], "Address not exist");
        uint location;
        for(uint i = 0; i < _staking.length; i++){
            if(_staking[i] == _stakeholder){
                location = i;
            }
        }
        _staking[location] = _staking[_staking.length - 1];
        _staking.pop();
    }
    
    //20% of 1B to 20 wallets => 1% per wallet, 80% token on contract
    constructor(string memory token_name, string memory token_symbol, address charityWallet, address marketingWallet, address[] memory stakeholders) ERC20(token_name, token_symbol){
        require(stakeholders.length == 12, "You need to input 12 wallet of holder");
        _ownerAddress = msg.sender;
        _mint(_ownerAddress, 1000000000*10**decimals());
        setCharityWallet(charityWallet);
	    setMarketingWallet(marketingWallet);
	    for(uint i = 0; i < stakeholders.length; i++){
	        addStakeholder(stakeholders[i]);
	        
	    }
	    initToken();
    }
    

    function initToken() private onlyOwner{
        require(firstInit, "Only send token one time");
        uint coinLeft = totalSupply() * 20 / 100;
        for(uint i = 0; i < _staking.length; i++){
            uint rndToken = randomToken(i)*10**decimals();
            if(coinLeft < rndToken || i == _staking.length - 1){
                _transfer(_msgSender(),_staking[i], coinLeft);
                break;
            }else{
                _transfer(_msgSender(),_staking[i], rndToken);
                coinLeft -= rndToken;
            }
        }
        firstInit = false;
    }
    
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        uint256 fees = amount * taxation / 100;
        _transfer(_msgSender(), recipient, amount - fees);
        _transfer(_msgSender(), _charityWallet, fees / 3);
        _transfer(_msgSender(), _marketingWallet, fees / 3);
        uint256 holderToken = (fees / (3*_staking.length));
        bool flag1=false;
        bool flag2=false;
        for(uint i = 0; i < _staking.length; i++){
            _transfer(_msgSender(), _staking[i], holderToken);
            if(_staking[i]==recipient){
                flag1=true;
            }
            if(_staking[i]==_msgSender()){
                uint256 balance=balanceOf(_msgSender());
                if(balance<=0){
                    flag2=true;
                }
            }
        }
        if(flag1==false){
            addStakeholder(recipient);
        }
        if(flag2==true){
            removeStakeholder(_msgSender());
        }
        
        return true;
    }
    function mint(uint256 amount) public virtual onlyOwner returns (bool) {
        _mint(_msgSender(), amount);
        return true;
    }
    function burn(uint256 amount) public virtual onlyOwner returns (bool) {
        _burn(_msgSender(), amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        uint256 fees = amount * taxation / 100;
        _transfer(sender, recipient, amount);
        _transfer(sender, _charityWallet, fees / 3);
        _transfer(sender, _marketingWallet, fees / 3);
        uint256 holderToken = (fees / (3*_staking.length));
        bool flag1=false;
        bool flag2=false;
        for(uint i = 0; i < _staking.length; i++){
            _transfer(_msgSender(), _staking[i], holderToken);
            if(_staking[i]==recipient){
                flag1=true;
            }
            if(_staking[i]==_msgSender()){
                uint256 balance=balanceOf(_msgSender());
                if(balance<=0){
                    flag2=true;
                }
            }
        }
        if(flag1==false){
            addStakeholder(recipient);
        }
        if(flag1==true){
            removeStakeholder(_msgSender());
        }
        uint256 currentAllowance = allowance(sender, _msgSender());
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);

        return true;
    }
    
    function listStakeholders() public view returns(address[] memory){
        return _staking;
    }
    
    
    function randomToken(uint nonce) public view returns(uint){
        return uint(keccak256(abi.encodePacked(block.timestamp, 
                                          block.difficulty, 
                                          nonce))) % 15000000;
    }
}
