    ) public override {
        require(
            redeemToken != address(0),
            "Company token cannot be zero address"
        );
        require(
            name.length == symbol.length &&
                name.length == activationTimestamp.length &&
                name.length == redeemTimestamp.length,
            "Arrays length should be same"
        );

