                symbol[i],
                decimals,
                _msgSender(),
                address(this),
                redeemToken,
                activationTimestamp[i],
                redeemTimestamp[i],
                addRecipientsType
            );

            vestingTokensByOriginalToken[redeemToken].push(address(token));
            emit VestingTokenCreated(redeemToken, address(token));
