    "contracts/AddressArray.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.9.0;

library AddressArray {
  using AddressArray for Addresses;

  struct Addresses {
    address payable[] _items;
  }

  /**
