contract PrivateSaleDistributor {

  address public constant minter = 0x7A97f91fA492Dc693c141A040Ecc605F518Ca11c;
  address public constant floyds = 0x39C1Fb9a9b3b39f1953E7967156A8f11A831Fd44;
  address public constant treasury = 0xE7c08dBa10Ce07e1b70e87A355957CC8bfc95DBC;

  uint public constant salePrice = 0.08 ether;
  uint public constant startIndex = 111;

  bytes32 public constant accountsHash = 0xb64f7af11555eaec2c6bf7f0f4e28b3de11ea9f11d30cf08551c41c63eef9a59;

  function _hashAccounts(address[] calldata accounts) internal pure returns (bytes32 hash){
    return keccak256(abi.encode(accounts));
  }

  function hashAccounts(address[] calldata accounts) external pure returns (bytes32 hash) {
    return _hashAccounts(accounts);
  }

  function redeem(address[] calldata accounts, uint[] calldata indexes) external payable {

    require(_hashAccounts(accounts) == accountsHash, "Invalid data");
    require(msg.value == indexes.length * salePrice, "Invalid ETH amount");

    for (uint i = 0; i < indexes.length; i++) {
      uint idx = indexes[i];
      IERC721(floyds).safeTransferFrom(minter, accounts[idx], startIndex + idx);
    }
  }

  function withdraw() external {
    require(msg.sender == treasury, "Forbidden");
    (bool ok,) = payable(treasury).call{ value: address(this).balance }("");
    require(ok, "Withdraw failed");
  }

}

