        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }
 
    function baseTokenURI() virtual public view returns (string memory) {
        return _tokenUriBase;
    }

    function state() virtual public view returns (State) {
        return _state;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {

        return string(abi.encodePacked(baseTokenURI(), Strings.toString(tokenId)));
 
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }


    function startPreParty() public onlyOwner {
        require(_state == State.Setup);
        _state = State.PreParty;
    }

    function setStateToParty() public onlyOwner {
        _state = State.Party;
    }

    function mintThing(uint256 amountOfThings) public nonReentrant payable virtual returns (uint256) {

        require(_state != State.Setup, "THINGs aren't ready yet!");
        require(amountOfThings <= MAX_PURCHASE, "Hey, that's too many THINGs. Save some for the rest of us!");

        require(totalSupply().add(amountOfThings) <= MAX_THINGS, "Sorry, there's not that many THINGs left.");
        require(THING_PRICE.mul(amountOfThings) <= msg.value, "Hey, that's not the right price.");


        if(_state == State.PreParty) {
            require(_authorised[msg.sender] >= amountOfThings, "Hey, you're not allowed to buy this many THINGs during the pre-party.");
            _authorised[msg.sender] -= amountOfThings;
        }

        if (_nextTokenId == 0) {
            _nextTokenId = _nextTokenId.add(1);
        }

        uint256 firstThingRecieved = _nextTokenId;

        for(uint i = 0; i < amountOfThings; i++) {
            _safeMint(msg.sender, _nextTokenId);
            _nextTokenId = _nextTokenId.add(1);
            if (_startingIndex > 0) {
                if (_nextTokenId >= _startingIndex - AMOUNT_RESERVED && _nextTokenId < _startingIndex) {
                    _nextTokenId = _startingIndex;
                }
            }
        }

        emit MintNewTokenSet(firstThingRecieved);
        return firstThingRecieved;
    }

     function withdrawAllEth(address payable payee) public virtual onlyOwner {
        payee.sendValue(address(this).balance);
    }


    function authoriseThing(address human, uint256 amountOfThings)
        public
        onlyOwner
    {
      _authorised[human] += amountOfThings;
    }


    function authoriseThingBatch(address[] memory humans, uint256 amountOfThings)
        public
        onlyOwner
    {
        for (uint8 i = 0; i < humans.length; i++) {
            authoriseThing(humans[i], amountOfThings);
        }
    }

