        receiverPercentages = receiverPercentages_;

        currentTeamBalance = new uint256[](receiverAddresses.length);

        for (uint8 i = 0; i < receiverAddresses.length; i++) {
            addressToIndex[receiverAddresses[i]] = i + 1;
        }
