        _symbol = symbol_;

        _decimals = 18;

        _dudesAddress = dudesAddress;
        _sistasAddress = sistasAddress;

        emissionStart = emissionStartTimestamp;
        emissionEnd = emissionStartTimestamp + (86400 * 365);

