// File contracts/creepy-kids/CreepyKidsRedemption.sol

pragma solidity 0.7.3;

contract CreepyKidsRedemption {
    using SafeMath for uint256;
    ICreepyKids public creepyKids;
    IERC721Enumerable public evilTeddyBear;

    uint256 public beginningOfRedemption;
    uint256 public constant MAX_SUPPLY = 10000;
    uint256 private devFee;
    address payable private dev;
    address private oracleEthUsd;
    mapping(uint256 => bool) public hasRedeemedCreepyKid;

    constructor(
        address _creepyKids,
        address _evilTeddyBear,
        address _oracleEthUsd,
        address payable _dev,
        uint256 _beginningOfRedemption
    ) {
        creepyKids = ICreepyKids(_creepyKids);
        evilTeddyBear = IERC721Enumerable(_evilTeddyBear);
        oracleEthUsd = _oracleEthUsd;

        beginningOfRedemption = _beginningOfRedemption;
        dev = _dev;
        devFee = 10; // used to cover work, maintainance and deployment costs
    }

    // fallback function can be used to mint CreepyKids
    receive() external payable {
        uint256 numOfCreepyKidss = msg.value.div(fee());

        mintNFT(numOfCreepyKidss);
    }

    /**
     * @dev Main sale function. Mints CreepyKids
     */
    function mintNFT(uint256 numberOfCreepyKidsToMint) public payable {
        require(
            block.timestamp >= beginningOfRedemption,
            "Redemption has not started"
        );

        (
            uint256 eligibleTeddys,
            uint256[] memory eligibleTeddysIds
        ) = numberOfEligibleEvilTeddyBearInAccount(
                msg.sender,
                numberOfCreepyKidsToMint
            );

        require(
            eligibleTeddys == numberOfCreepyKidsToMint,
            "Not enough Teddys eligible to redeem for Creepies"
        );

        require(
            creepyKids.totalSupply() <= MAX_SUPPLY,
            "Redemption has already ended"
        );

        require(
            creepyKids.totalSupply().add(numberOfCreepyKidsToMint) <=
                MAX_SUPPLY,
            "Exceeds MAX_SUPPLY"
        );

        require(
            fee().mul(numberOfCreepyKidsToMint) == msg.value,
            "Ether value sent is not correct"
        );

        for (uint256 i; i < numberOfCreepyKidsToMint; i++) {
            require(
                !hasRedeemedCreepyKid[eligibleTeddysIds[i]],
                "Teddy not eligible"
            );
            creepyKids.mint(msg.sender);
            hasRedeemedCreepyKid[eligibleTeddysIds[i]] = true;
        }

        forwardFunds(msg.value);
    }

    function numberOfEligibleEvilTeddyBearInAccount(
        address account,
        uint256 creepyKidsAsk
    ) public view returns (uint256 eTeddys, uint256[] memory eTeddysIds) {
        uint256 balanceOfUser = evilTeddyBear.balanceOf(account);

        eTeddysIds = new uint256[](creepyKidsAsk);

        for (uint256 index; index < balanceOfUser; index++) {
            if (
                !hasRedeemedCreepyKid[
                    evilTeddyBear.tokenOfOwnerByIndex(account, index)
                ]
            ) {
                eTeddysIds[eTeddys] = evilTeddyBear.tokenOfOwnerByIndex(
                    account,
                    index
                );
                eTeddys = eTeddys.add(1);
            }

            if (eTeddys == creepyKidsAsk) break;
        }
    }

    function fee() public view returns (uint256) {
        (, int256 price, , , ) = AggregatorV3Interface(oracleEthUsd)
            .latestRoundData();
        uint256 currentETHPriceInUSD = uint256(price).div(10**8); // price comes in 8 decimals

        uint256 res = devFee.mul(10000).div(currentETHPriceInUSD) * 10**18; // ETH has 18 decimals

        return res.div(10000);
    }

    function setDevFee(uint256 newFee) external {
        require(msg.sender == dev, "!dev");

        devFee = newFee;
    }

    function forwardFunds(uint256 funds) internal {
        (bool success, ) = dev.call{value: funds}("");
        require(success, "funds were not sent to dev");
    }
}
