library Buffer {
    function hasCapacityFor(bytes memory buffer, uint256 needed) internal pure returns (bool) {
        uint256 size;
        uint256 used;
        assembly {
            size := mload(buffer)
            used := mload(add(buffer, 32))
        }
        return size >= 32 && used <= size - 32 && used + needed <= size - 32;
    }
    
    function toString(bytes memory buffer) internal pure returns (string memory) {
        require(hasCapacityFor(buffer, 0), "Buffer.toString: invalid buffer");
        string memory ret;
        assembly {
            ret := add(buffer, 32)
        }
        return ret;
    }
    
    function append(bytes memory buffer, string memory str) internal view {
        require(hasCapacityFor(buffer, bytes(str).length), "Buffer.append: no capacity");
        assembly {
            let len := mload(add(buffer, 32))
            pop(staticcall(gas(), 0x4, add(str, 32), mload(str), add(len, add(buffer, 64)), mload(str)))
            mstore(add(buffer, 32), add(len, mload(str)))
        }
    }
    
    /**
     * value must be in the closed interval [-999, 999]
     * otherwise only the last 3 digits will be converted
     */
    function numbie(bytes memory buffer, int256 value) internal pure {
        require(hasCapacityFor(buffer, 4), "Buffer.numbie: no capacity");
        assembly {
            function numbx1(x, v) -> y {
                // v must be in the closed interval [0, 9]
                // otherwise it outputs junk
                mstore8(x, add(v, 48))
                y := add(x, 1)
            }
            function numbx2(x, v) -> y {
                // v must be in the closed interval [0, 99]
                // otherwise it outputs junk
                y := numbx1(numbx1(x, div(v, 10)), mod(v, 10))
            }
            function numbu3(x, v) -> y {
                // v must be in the closed interval [0, 999]
                // otherwise only the last 3 digits will be converted
                switch lt(v, 100)
                case 0 {
                    // without input value sanitation: y := numbx2(numbx1(x, div(v, 100)), mod(v, 100))
                    y := numbx2(numbx1(x, mod(div(v, 100), 10)), mod(v, 100))
                }
                default {
                    switch lt(v, 10)
                    case 0 { y := numbx2(x, v) }
                    default { y := numbx1(x, v) }
                }
            }
            function numbi3(x, v) -> y {
                // v must be in the closed interval [-999, 999]
                // otherwise only the last 3 digits will be converted
                if slt(v, 0) {
                    v := add(not(v), 1)
                    mstore8(x, 45)  // minus sign
                    x := add(x, 1)
                }
                y := numbu3(x, v)
            }
            let strIdx := add(mload(add(buffer, 32)), add(buffer, 64))
            strIdx := numbi3(strIdx, value)
            mstore(add(buffer, 32), sub(sub(strIdx, buffer), 64))
        }
    }
}

abstract contract ReentrancyGuard {
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;

    uint256 private _status;

    constructor() {
        _status = _NOT_ENTERED;
    }

    modifier nonReentrant() {
        // On the first call to nonReentrant, _notEntered will be true
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _status = _ENTERED;

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = _NOT_ENTERED;
    }
}
contract Squares is ERC721("Exceptional Squares", "ESQR"), Ownable, ReentrancyGuard {
    uint256 public id_count = 0;
    uint256 public mint_price = 0;
    mapping(uint256 => uint8[15]) public metadata;
    int256[][4] public trig;
    
    constructor() {
        mint();
        trig[0] = [100, 0, 0, 100, int(-100), 0, 0, int(-100), 100, 0];
        trig[1] = [100, 0,int(-15), 99,int(-96), int(-29),42, int(-91),83, 55,int(-67), 75,int(-64), int(-77),85, int(-52),39, 92,int(-97), 25,int(-11), int(-99),100, 4,int(-18), 98,int(-95), int(-32),46, int(-89),81, 58,int(-69), 72,int(-61), int(-79),87, int(-49),36, 93,int(-98), 22,int(-8), int(-100),100, 7,int(-22), 98,int(-93), int(-35)];
        trig[2] = [100, 0,int(-3), 100,int(-100), int(-6),9, int(-100),99, 12,int(-15), 99,int(-98), int(-17),20, int(-98),97, 23,int(-26), 97,int(-96), int(-29),32, int(-95),94, 34,int(-37), 93,int(-92), int(-40),42, int(-91),89, 45,int(-48), 88,int(-86), int(-50),53, int(-85),83, 55,int(-58), 82,int(-80), int(-60),62, int(-78),76, 64];
        trig[3] = [100, 0,int(-42), 91,int(-65), int(-76),96, int(-28),int(-15), 99,int(-84), int(-54),84, int(-54),14, 99,int(-96), int(-29),66, int(-75),41, 91,int(-100), int(-1),42, int(-91),65, 76,int(-96), 27,15, int(-99),83, 55,int(-85), 53,int(-13), int(-99),96, 30,int(-67), 75,int(-40), int(-92),100, 2,int(-43), 90,int(-64), int(-77)];
    }

    function claim() public onlyOwner {
    	address payable recep = payable(owner());
    	recep.transfer(address(this).balance);
    }

    function mint() payable public nonReentrant {
        require(id_count < 1024, "Squares: Reached maximum token count");
        require(msg.value >= mint_price, "Squares: Insufficient value to mint");

        _safeMint(_msgSender(), id_count);
    
        uint256 seed = uint256(keccak256(abi.encodePacked(block.timestamp, id_count, msg.sender)));
        uint8[15] storage digits; 
        uint8 i;
    
        for (i=0;i<15;i++) {
            uint8 digit = uint8(seed % 100);
            seed = seed / 100;
            digits[i] = digit;
        }
        metadata[id_count] = digits;

        id_count += 1;
        
        // refund value above mint price
    	if (msg.value > mint_price) {
	    	address payable refundee = payable(_msgSender());
	    	refundee.transfer(msg.value - mint_price);
    	}

        uint256 new_mint_price = SafeMath.div(SafeMath.mul(mint_price,1020),1000) ; //Grow by 2% each sale or min 0.01eth
        
        if (new_mint_price <= (mint_price + 1e16)) {
            new_mint_price = mint_price + 1e16;
        }
        
        mint_price = new_mint_price;

    }

   function viewMetadata(uint256 tokenId) public view returns (uint8[15] memory) {
        return metadata[tokenId];
   }

    function contractURI() public view returns (string memory) {
        return tokenURI(0);
    }

    function uriFromMetadata(uint8[15] memory md) private view returns (string memory) {
        uint256 i = 0;
        uint256 layer = 0;
        uint256 rand;
        int256 r; // Radius
        int256[2] memory origin = [int(200), int(200)];
        bytes memory buffer = new bytes(8192);
        Buffer.append(buffer, "data:application/json;utf8,%7B%22name%22%3A%20%22Exceptional%20Squares%22%2C%22description%22%3A%20%22Onchain%20Polygon%20Art%22%2C%22image%22%3A%22data%3Aimage%2Fsvg%2Bxml%3Butf8%2C%3Csvg%20xmlns%3D'http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg'%20viewBox%3D'0%200%20400%20400'%20height%3D'400px'%20width%3D'400px'%3E%3Cg%3E%3Crect%20x%3D'0'%20y%3D'0'%20width%3D'400'%20height%3D'400'%20stroke%3D'black'%20fill%3D'black'%2F%3E");
        int[] memory triFac;  //[1, 0, 0, 1, int(-1), 0, 0, int(-1), 1, 0]

        for (layer=0;layer<5;layer++) {
            
            rand = md[layer*3] % 4;      //Get shape
            triFac = trig[rand];
            
            rand = md[layer*3 + 1] % 90 + 10;      //Get radius
            r = 2 * int(rand);

            Buffer.append(buffer, "%3Cpath%20d%3D'M%20");
            Buffer.numbie(buffer, origin[0] + r * triFac[0] / 100);
            Buffer.append(buffer, '%20');
            Buffer.numbie(buffer, origin[1] + r * triFac[1] / 100);
            Buffer.append(buffer, '%20');

            for (i=2; i<triFac.length; i+=2) {
                Buffer.append(buffer, 'L');
                Buffer.numbie(buffer, origin[0] + r * triFac[i] / 100);
                Buffer.append(buffer, '%20');
                Buffer.numbie(buffer, origin[1] + r * triFac[i+1] / 100);
                Buffer.append(buffer, '%20');
            }

            Buffer.append(buffer, "'%20stroke%3D'rgb(");
            Buffer.numbie(buffer, 255-md[15-layer*3-1]);
            Buffer.append(buffer, '%2C');
            Buffer.numbie(buffer, 255-md[15-layer*3-2]);
            Buffer.append(buffer, '%2C');
            Buffer.numbie(buffer, 255-md[15-layer*3-3]);
            Buffer.append(buffer, ")'%20fill%3D'transparent'%20stroke-width%3D'1.5'%2F%3E");
           
        }

        Buffer.append(buffer, '%3C%2Fg%3E%3C%2Fsvg%3E%22%7D'); 
        return Buffer.toString(buffer);
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        return uriFromMetadata(metadata[tokenId]);
    }

    function getMintPrice() public view returns (uint256) {
        return mint_price;
    }
}
