        require(hasRole(PAUSER_ROLE, _msgSender()), "ERC721PresetMinterPauserAutoId: must have pauser role to unpause");
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override(ERC721Upgradeable, ERC721EnumerableUpgradeable, ERC721PausableUpgradeable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControlEnumerableUpgradeable, ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
    uint256[48] private __gap;


    ////////////////////////////////////////////////////////////
    // GENERATE AND CLAIM WITH MINTING
    ////////////////////////////////////////////////////////////

    // total claimed using claimNFT() function (not mint() function)
    uint256 public totalClaimed;

    // counters for various claim conditions/limits
    uint256 public claimedLOOT;
    uint256 public claimedBLOOT;
    uint256 public claimedsOHM;
    uint256 public claimedMOVE;

    uint256 constant LIMIT_LOOT = 4000;
    uint256 constant LIMIT_BLOOT = 2000;
    uint256 constant LIMIT_SOHM = 2000;
    uint256 constant LIMIT_MOVE = 999;


    // storage of dice types for dices claimed
    // there are 9999 vaults to be created, so the seed is not predefined by tokenId
    // (so it cannot be easily predicted), but by a two random dice rolls
    // 0-999, multiplied by block number during claim, giving a unique seed
    // 000...000<block_number><0-999><0-999>
    // that is stored for every NFT ID
    mapping(uint256 => uint256) public accountSeeds;

    uint256 constant FIELD_BANKNAME = 0;
    uint256 constant FIELD_MANAGER = 1;
    uint256 constant FIELD_TIER = 2;
    uint256 constant FIELD_ACCTYPE = 3;

    // string data
    string[] private bank_names;
    string[] private bank_types;
    string[] private mngr_names;
    string[] private mngr_lastnames;
    string[] private tiers;
    string[] private account_types;
    string[] private nicknames;
    string[] private locations;
    
    function randomSeed() internal view returns (uint256) {
        // perform dice roll using Dice contract
        uint256 dice = IDice(diceContract).roll(1000) - 1; // returns 1-1000, so subtract 1
        uint256 seed = block.number * 1000000000 + dice * 1000000 + _tokenIdTracker.current();
        return seed;
    }
    

    // public getters for separate account details
    // they have custom logic based on 'greatness factor'

    function getBankName(uint256 tokenId) public view returns (string memory res) {
        (res, ) = peek(accountSeeds[tokenId], FIELD_BANKNAME, false, 0);
    }
    
    function getManager(uint256 tokenId) public view returns (string memory res) {
        (res, ) = peek(accountSeeds[tokenId], FIELD_MANAGER, false, 0);
    }

    function getClientTier(uint256 tokenId) public view returns (string memory res) {
        (res, ) = peek(accountSeeds[tokenId], FIELD_TIER, false, 0);
    }

    function getAccountType(uint256 tokenId) public view returns (string memory res) {
        (res, ) = peek(accountSeeds[tokenId], FIELD_ACCTYPE, false, 0);
    }

    // NOTE: array lengths should differ to avoid having the same pair of connected attributes
    function peek(uint256 seed, uint256 field, bool svgFormat, uint256 offset) internal view returns (string memory, uint256 lines) {
        string memory keyPrefix;
        if (field == FIELD_BANKNAME) {
            keyPrefix = "BANKNAME";
        } else if (field == FIELD_MANAGER) {
            keyPrefix = "MANAGER";
        } else if (field == FIELD_TIER) {
            keyPrefix = "TIER";
        } else if (field == FIELD_ACCTYPE) {
            keyPrefix = "ACCTYPE";
        }
        uint256 deterministic = uint256(keccak256(abi.encodePacked(keyPrefix, toString(seed))));
        lines = 0;

        string memory output;
        if (field == FIELD_TIER) {
            output = tiers[deterministic % tiers.length];
        } else if (field == FIELD_ACCTYPE) {
            output = account_types[deterministic % account_types.length];
        } else {
            uint256 greatness = deterministic % 21;

            if (field == FIELD_BANKNAME) {
                output = bank_names[deterministic % (bank_names.length)];
            } else if (field == FIELD_MANAGER) {
                if (deterministic % mngr_names.length >= mngr_names.length - 3) {
                    // last 3 manager names come without last name
                    output = mngr_names[deterministic % mngr_names.length];
                } else {
                    output = string(abi.encodePacked(mngr_names[deterministic % mngr_names.length], " ", mngr_lastnames[deterministic % mngr_lastnames.length]));
                }
            }

            // with greatness > 19 add nickname prefix
            if (greatness >= 19) {
                if (greatness == 19) {
                    output = string(abi.encodePacked('"', nicknames[deterministic % (nicknames.length)], '" ', output));
                } else {
                    output = string(abi.encodePacked('"', nicknames[deterministic % (nicknames.length)], '" ', output, " +1"));
                }
            }

            // with greatness > 15 add location
            if (greatness > 15) {
                if (svgFormat) {
                    lines = 1;
                    output = string(abi.encodePacked(output, '</text><text x="10" y="', toString(offset + (20 * lines)), '" class="base">from ', locations[deterministic % (locations.length)]));
                } else {
                    output = string(abi.encodePacked(output, ' from ', locations[deterministic % (locations.length)]));
                }
            }
        }

        if (svgFormat) {
            output = string(abi.encodePacked('<text x="10" y="', toString(offset), '" class="base">', output, '</text>'));
        }

        return (output, lines);
    }


    function claimNFT() public nonReentrant {
        // check claim requirements
        require(_tokenIdTracker.current() < 9999, "claim limit reached"); // tokenIds 0-11999 available

        bool claimable = false;
        // check LOOT, BLOOT, sOHM, check MOVE (incl. staked in Smart Treasury) in order
        if (IERC721Upgradeable(lootContract).balanceOf(msg.sender) > 0 && claimedLOOT < LIMIT_LOOT) {
            claimable = true;
            claimedLOOT = claimedLOOT + 1;
        } else if (IERC721Upgradeable(blootContract).balanceOf(msg.sender) > 0 && claimedBLOOT < LIMIT_BLOOT) {
            claimable = true;
            claimedBLOOT = claimedBLOOT + 1;
        } else if (IERC20Upgradeable(sohmContract).balanceOf(msg.sender) > 0 && claimedsOHM < LIMIT_SOHM) {
            claimable = true;
            claimedsOHM = claimedsOHM + 1;
        } else {
            (uint256 stakedMove, ) = ISmartTreasury(stContract).userInfoMove(msg.sender);
            (uint256 stakedMoveLP, ) = ISmartTreasury(stContract).userInfoMoveEthLP(msg.sender);
            if ((IERC20Upgradeable(moveContract).balanceOf(msg.sender) > 0 || IERC20Upgradeable(moveLPContract).balanceOf(msg.sender) > 0
                || stakedMove > 0 || stakedMoveLP > 0) && claimedMOVE < LIMIT_MOVE) {
                    claimable = true;
                    claimedMOVE = claimedMOVE + 1;
                }
        }

        require(claimable, "claim conditions not met");

        // mint a new NFT
        accountSeeds[_tokenIdTracker.current()] = randomSeed();
        _safeMint(_msgSender(), _tokenIdTracker.current());
        _tokenIdTracker.increment();
        totalClaimed = totalClaimed + 1;
    }

    ////////////////////////////////////////////////////////////
    // TOKEN METADATA
    ////////////////////////////////////////////////////////////

    function tokenURI(uint256 tokenId) override public view returns (string memory) {
        uint256 seed = accountSeeds[tokenId];
        string[6] memory parts;
        uint256 lines;
        parts[0] = '<svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMinYMin meet" viewBox="0 0 350 350"><style>.base { fill: #C0ED31; font-family: serif; font-size: 14px; }</style><rect width="100%" height="100%" fill="#100A20" />';

        uint256 offset = 20;
        (parts[1], lines) = peek(seed, FIELD_BANKNAME, true, offset);
        offset = offset + (lines+1) * 20;
        (parts[2], lines) = peek(seed, FIELD_MANAGER, true, offset);
        offset = offset + (lines+1) * 20;
        (parts[3], lines) = peek(seed, FIELD_TIER, true, offset);
        offset = offset + (lines+1) * 20;
        (parts[4], lines) = peek(seed, FIELD_ACCTYPE, true, offset);

        parts[5] = '</svg>';

        string memory output = string(abi.encodePacked(parts[0], parts[1], parts[2], parts[3], parts[4], parts[5]));
        
        string memory json = Base64.encode(bytes(string(abi.encodePacked('{"name": "Account #', toString(seed), '", "description": "Every adventurer needs a safe place for their earnings in Metaverse. Vaults are randomized accounts generated and stored on chain. But Vaults are more than NFTs. Once a day Vault owners can roll dice. Every week, the vault with the highest score gets to open the vault and claim the prize. The metagame in metaverse.", "image": "data:image/svg+xml;base64,', Base64.encode(bytes(output)), '"}'))));
        output = string(abi.encodePacked('data:application/json;base64,', json));

        return output;
    }

    function toString(uint256 value) internal pure returns (string memory) {
    // Inspired by OraclizeAPI's implementation - MIT license
    // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}
