        require(tokenIdToStkStruct[_tokenId].availableNotes > 0, "Notes Spent. refill");
        require(bytes(_message).length < 256, "Wrong message Length");


        // Only genesis can can set burnable
        if (_burnable == false && tokenIdToStkStruct[_tokenId].genesis == false){
            revert("only genesis");
        }

        // Decrement available notes
        tokenIdToStkStruct[_tokenId].availableNotes = tokenIdToStkStruct[_tokenId].availableNotes.sub(1);

        // Get new ID BG+FG+Hash
        uint24 bg_color = uint24(get_bg_color_from_pad(_tokenId) & 0xffffff);
        uint24 fg_color = uint24(get_fg_color_from_pad(_tokenId) & 0xffffff);
        uint24 hash = hash(_message, _tokenId, totalSentCounter);

        // concat colors
        uint48 colors = uint48(uint32(bg_color)) << 24 | uint32(fg_color);
        // concat colors with hash
        uint256 newTokenId = uint256(uint72(colors)) << 48 | uint72(hash);


        // Create the token structure
        tokenIdToStkStruct[newTokenId] = StickyToken({
        availableNotes : 0,
        message : _message,
        pad : false,
        burnable : _burnable,
        genesis: false
