        jsonBuffer = abi.encodePacked(jsonBuffer, '"external_url": "https://stickynft.com",');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"image": "data:image/svg+xml;utf8,', generateSvg(bgHexCode, fgHexCode, cleanMessage), '",');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"attributes": [');
        jsonBuffer = abi.encodePacked(jsonBuffer, '{"trait_type":"Birthday","value":', Strings.toString(block.timestamp), ',"display_type":"date"},');
        jsonBuffer = abi.encodePacked(jsonBuffer, '{"trait_type":"Background","value":"', bgHexCode, '"},');
        jsonBuffer = abi.encodePacked(jsonBuffer, '{"trait_type":"Font","value":"', fgHexCode, '"}],');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"seller_fee_basis_points": 100,');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"fee_recipient": "0x', toAsciiString(address(this)), '",');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"animation_url": "https://view.stickynft.com/', Strings.toString(tokenId), '",');
        jsonBuffer = abi.encodePacked(jsonBuffer, '"message": "', string(cleanMessage), '"}');

        return string(jsonBuffer);
    }


    /*** Remove bad characters from message.*/
    function escapeMessage(bytes memory message) private pure returns (bytes memory clean) {
        bytes1 doubleQuote = bytes1("\"");
        bytes1 backslash = bytes1("\\");

        for (uint256 counter = 0; counter < message.length; counter++) {
            if (message[counter] != doubleQuote || message[counter] != backslash) {
                clean = abi.encodePacked(clean, message[counter]);
            }
        }
        return clean;
    }

    /*** Generate the SVG file */
    function generateSvg(string memory bgHexCode, string memory fgHexCode, bytes memory message) private pure returns (bytes memory svg) {
        svg = abi.encodePacked(svg, "<svg xmlns='http://www.w3.org/2000/svg' width='614' height='614' >");
        svg = abi.encodePacked(svg, "<rect width='1080' height='1080' fill='", bgHexCode, "'/>");

        // Characters to watch for
        bytes1 space = bytes1(" ");
        if (message.length > 0) {
            svg = abi.encodePacked(svg, "<text x='25' y='20' font-family='serif' font-size='32' fill='", fgHexCode, "' >");
            svg = abi.encodePacked(svg, "<tspan x='15' dy='1.2em' >");
            for (uint256 counter = 0; counter < message.length; counter++) {
                if (counter.mod(28) == 0 && counter > 0) {
                    if (message[counter] != space) {
                        svg = abi.encodePacked(svg, message[counter], "_</tspan>");
                    } else {
                        svg = abi.encodePacked(svg, message[counter], "</tspan>");
                    }
                    svg = abi.encodePacked(svg, "<tspan x='15' dy='1.2em' >");
                } else {
                    svg = abi.encodePacked(svg, message[counter]);
                }
            }
            svg = abi.encodePacked(svg, "</tspan>");
            svg = abi.encodePacked(svg, "</text>");
        } else {
            svg = abi.encodePacked(svg, "<text x='25' y='25' font-family='Verdana' font-size='14' fill='", fgHexCode, "' >");
            svg = abi.encodePacked(svg, "This is a Sticky Note Pad </text>");
        }

        return abi.encodePacked(svg, "</svg>");
    }

