    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory) public virtual override {
        transferFrom(from, to, tokenId);
    }


    function transferFrom(address from, address to, uint256 tokenId) public virtual override {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        require(tokenIdToStkStruct[tokenId].pad == true, "cant transfer a note");

        // Remove the token from the owner list
        for (uint i = 0; i < ownerToToken[from].length; i++) {
            if (ownerToToken[from][i] == tokenId) {
                ownerToToken[from][i] = ownerToToken[from][ownerToToken[from].length - 1];
                ownerToToken[from].pop();
                ownerToToken[to].push(tokenId);

                // if we dont find the token then we can't transfer.
                _transfer(from, to, tokenId);

                break;
            }
        }

    }

    function toAsciiString(address x) internal view returns (string memory) {
        bytes memory s = new bytes(40);
        for (uint i = 0; i < 20; i++) {
            bytes1 b = bytes1(uint8(uint(uint160(x)) / (2 ** (8 * (19 - i)))));
            bytes1 hi = bytes1(uint8(b) / 16);
            bytes1 lo = bytes1(uint8(b) - 16 * uint8(hi));
            s[2 * i] = char(hi);
            s[2 * i + 1] = char(lo);
        }
        return string(s);
    }

    function char(bytes1 b) internal pure returns (bytes1 c) {
        if (uint8(b) < 10) return bytes1(uint8(b) + 0x30);
        else return bytes1(uint8(b) + 0x57);
    }


    function hash(string memory message, uint256 parent_token, uint256 seed) public pure returns (uint24)
    {
        return uint24(uint256(keccak256(abi.encodePacked(message, parent_token, seed))) & 0xffffff);
    }

