contract ETHBRIDGE {
    using SafeMath for uint256;
    
    IERC20 public token;
    address payable public owner_;
    uint256 public deadline = 1633132799;
    
    constructor() {
        owner_ = payable(msg.sender);
        token = IERC20(0xe5a09784b16E1065C37dF14c6e2f06fDcE317a1b);
    }
    
    modifier onlyOwner {
        require(msg.sender == owner_, 'Caller is not the owner');
        _;
    }
    
    event bridgeTransfer(address indexed sender, address receiver, uint256 amount);
    
    // This is used to depost the fund
    function tokenDeposit(uint256 amount) public {
        require(block.timestamp <= deadline, "Deadline Passed!");
        require(amount > 0, "ERC20 : Zero Amount Not Allowed!");
        uint256 allowance = token.allowance(msg.sender, address(this));
        require(allowance >= amount, "low allowance");
        token.transferFrom(msg.sender, address(this), amount);
        emit bridgeTransfer(msg.sender,address(this), amount);
    }
    
    // This is Use by Owner to withdraw amount
    function Withdraw() public onlyOwner {
        require(msg.sender == owner_,"ERC20 : Only Owner Allowed!");
        IERC20(token).transfer(msg.sender , token.balanceOf(address(this)));
    }
    
    // This is used to change the deadline of tokenDeposit
    function ChangeDeadline(uint256 time) public onlyOwner {
        deadline = time;
    }
}
