      value = msg.value;
    }
    transferTokenAndValue(_erc721Address, _erc20Address, msg.sender, value, _tokenId, _from, _to, _receiveAddresses, _receiveFees);
  }
  

  function RoiyaltysplitERC20(address _erc20address ,address payable[] calldata _splitAddresses ,uint[] calldata _splitFees) external payable {
    require(msg.sender == owner() || getWhitelistinvoker(msg.sender),'Not have call permission');
    require(_splitAddresses.length > 0 && _splitFees.length > 0 ,'No split destination address or No split Fee');
    require(_splitAddresses.length == _splitFees.length,'Defference element num splitaddress and splitfees array');
    if(_erc20address != address(0x0)){
      ERC20 ftcontract = ERC20(_erc20address);
      for(uint i = 0; i < _splitAddresses.length ; i++){
        ftcontract.transferFrom(msg.sender, _splitAddresses[i], _splitFees[i]);
      }
    } else {
      uint value = msg.value;
      for(uint i = 0; i < _splitAddresses.length ; i++){
        value = value.sub(_splitFees[i]);
        _splitAddresses[i].transfer(_splitFees[i]);
      }
      msg.sender.transfer(value);
    }
  }

