  function uintToStr(uint _i) internal pure returns (string memory _uintAsString) {
    uint number = _i;
    if (number == 0) {
      return "0";
    }
    uint j = number;
    uint len;
    while (j != 0) {
      len++;
      j /= 10;
    }
    bytes memory bstr = new bytes(len);
    uint k = len - 1;
    while (number != 0) {
      bstr[k--] = byte(uint8(48 + number % 10));
      number /= 10;
    }
    return string(bstr);
  }

