contract Abraham is ERC20 {

    uint256 internal precio = 536040696209656;
    uint256 private constant incremento = 52448217;
    address private constant carteraFundadora1 = 0x7Dd945c8a80DB248BF92baD7241D538369B0D271;
    address private constant carteraFundadora2 = 0x4D4cb0906374F430D3B5954201A528c7CfEffB6F;
    address private constant carteraFundadora3 = 0x6547Fe63999D33445D50A90eCA33436c4F8Bd663;

    constructor() ERC20("Abraham", "Abraham") {
        _mint(deployer, 18000000 * 10 ** decimals()); 
        _mint(carteraFundadora1, 1000000 * 10 ** decimals());
        _mint(carteraFundadora2, 1000000 * 10 ** decimals());
        _mint(carteraFundadora3, 1000000 * 10 ** decimals());
    }

    function comprar() public payable returns (bool){
        require(msg.value > 0, "Tienes que mandar una cantidad de ETH mayor a 0");
        precio = precio + incremento * ((msg.value * 10 **decimals()) / precio) / 10 ** decimals();
        transferFrom(deployer, msg.sender, (msg.value *10**decimals()) / precio);
        return true;
    }

    function vender(uint256 cantidadTokensAVender) public payable returns (bool){
        require(cuentaFundadoraDesbloqueada(msg.sender),"Los tokens de esta cartera estan bloqueados hasta el 27/07/2024");
        require(cantidadTokensAVender > 0, "Tienes que enviar cantidad de tokens mayor a 0");
        transferFrom(msg.sender, deployer, cantidadTokensAVender * 10 ** decimals());
        address payable sender = payable(msg.sender);
        precio = precio - incremento * cantidadTokensAVender;
        sender.transfer(cantidadTokensAVender * precio);
        return true;
    }

    function venderTodo() public payable returns (bool){
        require(cuentaFundadoraDesbloqueada(msg.sender),"Los tokens de esta cartera estan bloqueados hasta el 27/07/2024");
        uint256 cantidadTokensAVender = balanceOf(msg.sender);
        transferFrom(msg.sender, deployer, cantidadTokensAVender);
        address payable sender = payable(msg.sender);
        precio = precio - incremento * (cantidadTokensAVender / 10 ** decimals());
        sender.transfer((cantidadTokensAVender / 10 ** decimals()) * precio);
        return true;
    }

    function cuantoEthTengoQueEnviar(uint256 cantidadDeTokensQueQuieroComprar) public view returns(uint256){
        return ((precio + (incremento * cantidadDeTokensQueQuieroComprar)) * cantidadDeTokensQueQuieroComprar);
    }

    function cuentaFundadoraDesbloqueada(address carteraIntento) private view returns (bool){
        if((carteraIntento == carteraFundadora1 || carteraIntento == carteraFundadora2 || carteraIntento == carteraFundadora3) && block.timestamp < 1722010122)
        {
            return false;
        }
        return true;
    }
}"
