contract DinoSwap is ERC721Holder, Ownable {
    IERC721 public inToken;
    IERC721 public outToken;
    
    bool private _paused;
    event Paused(address account);
    event Unpaused(address account);

    uint256 public lastTokenIdSent;

    mapping(address => bool) public presalerList;
    mapping(address => uint256) public presalerListPurchases;
    uint256 public presalePurchaseLimit = 1;

    constructor(address _inToken, address _outToken) {
        inToken = IERC721(_inToken);
        outToken = IERC721(_outToken);
        _paused = false;
        lastTokenIdSent = 0;
    }

    function swap(uint256 _tokenId) external whenNotPaused {
        require(lastTokenIdSent < 555, "Maximum number of swaps reached");
        require(presalerList[msg.sender], "NOT QUALIFIED");
        require(presalerListPurchases[msg.sender] + 1 <= presalePurchaseLimit, "EXCEEDED ALLOCATION");

        presalerListPurchases[msg.sender]++;

        inToken.transferFrom(msg.sender, address(this), _tokenId);
        outToken.transferFrom(address(this), msg.sender, lastTokenIdSent);
        lastTokenIdSent = lastTokenIdSent + 1;

    }
 

    function addToPresaleList(address[] calldata entries) external onlyOwner {
        for(uint256 i = 0; i < entries.length; i++) {
            address entry = entries[i];
            require(entry != address(0), "Cannot add zero address");
            require(!presalerList[entry], "Cannot add duplicate address");

            presalerList[entry] = true;
        }   
    }

    function removeFromPresaleList(address[] calldata entries) external onlyOwner {
        for(uint256 i = 0; i < entries.length; i++) {
            address entry = entries[i];
            require(entry != address(0), "Cannot remove zero address");
            
            presalerList[entry] = false;
        }
    }

    function isPresaler(address addr) external view returns (bool) {
        return presalerList[addr];
    }
    
    function presalePurchasedCount(address addr) external view returns (uint256) {
        return presalerListPurchases[addr];
    }

    function adminWithdrawETH() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function adminWithdrawERC20(address token) external onlyOwner {
        uint256 amount = IERC20(token).balanceOf(address(this));
        IERC20(token).transfer(msg.sender, amount);
    }

    function adminWithdrawERC721(address token, uint256 _tokenId) external onlyOwner {
        IERC721(token).transferFrom(address(this), msg.sender, _tokenId);
    }

    function adminWithdrawERC721Multi(address token, uint256[] calldata _tokenIds) external onlyOwner {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            IERC721(token).transferFrom(address(this), msg.sender, _tokenIds[i]);
        }
    }

    function setPresalePurchaseLimit(uint256 _newPresalePurchaseLimit) external onlyOwner {
        presalePurchaseLimit = _newPresalePurchaseLimit;
    }

    function pause() external onlyOwner{
        _pause();
    }

    function unpause() external onlyOwner{
        _unpause();
    }

    function paused() public view virtual returns (bool) {
        return _paused;
    }

    modifier whenNotPaused() {
        require(!paused(), "Pausable: paused");
        _;
    }
    
    modifier whenPaused() {
        require(paused(), "Pausable: not paused");
        _;
    }

    function _pause() internal virtual whenNotPaused {
        _paused = true;
        emit Paused(_msgSender());
    }

    function _unpause() internal virtual whenPaused {
        _paused = false;
        emit Unpaused(_msgSender());
    }
}
