/** 
 * @dev External interface of a SimpleMarketplace that lazily sells bundles
 */
interface ILazyMarketplace {
    /**
     * @dev Emitted when a created bundle is withdrawn from the marketplace
     * and can no longer be bought.
     */
    event BundleCancelled(bytes32 hash);

