/**
 * @dev Implementation of {ILazyMarketplace} that allows any address to sign bundles
 * of tokenIds for any ERC721 contractAddress to sell at a certain price.
 *
 * The marketplace also relies on a payoutContract to help split any fees or royalties
 * that an address can setup for the marketplace.
 */
contract LazyMarketplace is Ownable, ILazyMarketplace {

    /**
     * @dev Bundle structure that includes:
     * - what contract are the tokenIds from
     * - who is selling those tokenIds
     * - what price are they selling it at
     * - what active version are they selling for
     * - what tokenIds are in the bundle
     */
    struct Bundle {
        address contractAddress;
        address creatorAddress;
        uint256 price;
        uint256 version;
        uint256[] tokenIds;
    }

    /**
     * @dev Royatly structure that includes:
     * - What addresses should get royalties
     * - How many BPS does every address get
     */
    struct Royalty {
        address[] addresses;
        uint256[] bps;
    }

    /* @dev see {IEaselyPayout} for more */
    address public payoutContractAddress;

    /**
     * @dev Setting a min price so that users cannot accidentally
     * sell their tokens at a price that is off by a conversion rate.
     * The min WEI price below is equivalent to ETH 0.000001
     *
     * WEI has a conversion rate of:
     * 1 ETH = 1_000_000_000 GWEI
     * 1 GWEI = 1_000_000_000 WEI
     */
    uint256 public constant MIN_WEI_PRICE = 1_000_000_000_000;
    /* Max number of tokens that can be in a bundle */
    uint256 public constant MAX_BUNDLE_SIZE = 25; 
    /* Max number of addresses a seller can split royalties to */
    uint256 public constant MAX_ROYALTIES = 4;
    /* maximum total BPS among the royalty addresses */
    uint256 public constant BPS_MAX = 9_500;

    /* Cancelled or finalized bundles by hash to help determine buyabliity */
    mapping(bytes32 => bool) private _cancelledOrFinalizedBundles;

    /* Mapping to get the current active bundle version of each creator  */
    mapping(address => uint256) private _creatorBundleVersion;
    /* Royalties that can get set by each address */
    mapping(address => Royalty) private _royalties;

    /**
     * @dev Constructor function just takes in the payoutContractAddress
     */
    constructor(address payoutContractAddress_) {
        payoutContractAddress = payoutContractAddress_;
    }

    /**
     * @dev Checks if the inputted values of a bundle makes sense.
     *
     * Do not allow low prices because it's likely off a conversion factor.
     * Do not allow bundles to be too big due to the cost of gas to check and transfer.
     * Only honor bundles signed with the current active version of the creator.
     */
    function _isBundleValid(Bundle memory bundle) internal view returns (bool) {
        if (bundle.price < MIN_WEI_PRICE) {
            return false;
        }

        if (bundle.tokenIds.length > MAX_BUNDLE_SIZE) {
            return false;
        }

        if (_creatorBundleVersion[bundle.creatorAddress] != bundle.version) {
            return false;
        }

        return true;
    }

    /**
     * @dev Checks if the tokens can be traded by this contract.
     *
     * contract conform to the {IERC721} interface
     * this contract must be approved to transfer all tokens of a creator
     * and those tokens must exist and belong to the creator.
     */
    function _isBundleTransferrable(Bundle memory bundle) internal view returns (bool) {
        IERC721 nonFungibleContract = IERC721(bundle.contractAddress);

        bool isApproved = nonFungibleContract.isApprovedForAll(bundle.creatorAddress, address(this));

        uint256[] memory tokenIds = bundle.tokenIds;
        uint256 numTokens = tokenIds.length;
        // Cannot sell the bundle if the owner no longer owns all the tokens
        for (uint256 i = 0; i < numTokens; i++) {
            if(nonFungibleContract.ownerOf(tokenIds[i]) != bundle.creatorAddress) {
                return false;
            }
            if(!isApproved && nonFungibleContract.getApproved(tokenIds[i]) != address(this)) {
                return false;
            }
        }

        return true;
    }

    /**
     * @dev Checks if a hash matches the signature and that the creator 
     * is the one who signed the transaction.
     */
    function _isBundleSigned(
        address creatorAddress, 
        bytes32 hash, 
        uint8 v, 
        bytes32 r, 
        bytes32 s
    ) internal view returns (bool) {
        if(_cancelledOrFinalizedBundles[hash]) {
            return false;
        }

        return ecrecover(hash, v, r, s) == creatorAddress;
    }

    /**
     * toEthSignedMessageHash
     * @dev prefix a bytes32 value with "\x19Ethereum Signed Message:32"
     * and hash the result
     */
    function toEthSignedMessageHash(bytes32 hash) public pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }    

    /**
     * @dev Hash an order, returning the hash that a client must sign, including the standard message prefix
     * @param bundle Bundle to hash
     * @return Hash of message prefix and order hash per Ethereum format
     */
    function _hashToCheck(Bundle memory bundle) internal view returns (bytes32) {
        return toEthSignedMessageHash(_hashBundle(bundle));
    }

    /**
     * @dev Hash an order, returning the canonical order hash, without the message prefix
     * @param bundle Order to hash
     * @return Hash of order
     */
    function _hashBundle(Bundle memory bundle) internal view returns (bytes32) {
        return keccak256(abi.encode(address(this), block.chainid, bundle.contractAddress, bundle.creatorAddress, bundle.price, bundle.version, bundle.tokenIds));
    }

    /**
     * @dev see {hashToSign} in the interface
     */
    function hashToSign(
        address contractAddress,
        address creatorAddress,
        uint256 price,
        uint256 version,
        uint256[] memory tokenIds
    ) external view override returns (bytes32) {
        Bundle memory bundle = Bundle(contractAddress, creatorAddress, price, version, tokenIds);
        require(_isBundleValid(bundle), "This is not a valid bundle");

        return _hashBundle(bundle);
    }

    /**
     * @dev see {isBuyable} in the interface
     */
    function isBuyable(
        address contractAddress,
        address creatorAddress,
        uint256 price,
        uint256 version,
        uint256[] memory tokenIds,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external view override returns (bool) {
        Bundle memory bundle = Bundle(contractAddress, creatorAddress, price, version, tokenIds);
        bytes32 hash = _hashToCheck(bundle);

        return _isBundleValid(bundle) 
            && _isBundleTransferrable(bundle) 
            && _isBundleSigned(bundle.creatorAddress, hash, v, r, s);
    }

    /**
     * @dev Returns the current bundle version of a creatorAddress.
     * Any signed bundles that use a different version will not be buyable.
     */
    function getActiveBundleVersion(address creatorAddress) external view returns (uint256) {
        return _creatorBundleVersion[creatorAddress];
    }

    /**
     * @dev Allows any creator to set royalties for their bundle sales to 
     * other addresses
     */
    function setRoyalties(address[] memory addresses, uint256[] memory bps) external {
        require(addresses.length == bps.length, "Improper royalties input");
        require(addresses.length <= MAX_ROYALTIES, "Too many royalties");

        uint256 sum = 0;
        for (uint256 i = 0; i < bps.length; i++) {
            sum = sum + bps[i];
        }
        require(sum <= BPS_MAX, "BPS too high");

        Royalty memory royalty = Royalty(addresses, bps);
        _royalties[_msgSender()] = royalty;
    }

    /**
     * @dev Allows a creator to update their active version at any time.
     * Is a much cheaper way to invalidate everything a creator has signed
     * compared to individually calling cancelBundle.
     */
    function updateBundleVersion(uint256 activeVersion) external {
        _creatorBundleVersion[_msgSender()] = activeVersion;
    }

    /**
     * @dev see {cancelBundle} in the interface
     */
    function cancelBundle(
        address contractAddress,
        address creatorAddress,
        uint256 price,
        uint256 version,
        uint256[] memory tokenIds
    ) external override {
        address sender = _msgSender();
        // We don't need to verify the signature if the sender matches the creatorAddress
        require(sender == creatorAddress, "Only creator can cancel this bundle");

        Bundle memory bundle = Bundle(contractAddress, creatorAddress, price, version, tokenIds);
        require(_isBundleValid(bundle), "This bundle is already invalid");

        bytes32 hash = _hashToCheck(bundle);

        _cancelledOrFinalizedBundles[hash] = true;

        emit BundleCancelled(hash);
    }

    /**
     * @dev see {buyBundle} in the interface
     */
    function buyBundle(
        address contractAddress,
        address creatorAddress,
        uint256 price,
        uint256 version,
        uint256[] memory tokenIds,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable override {
        uint256 msgValue = msg.value;
        require(msgValue >= price, "Msg value too small");

        Bundle memory bundle = Bundle(contractAddress, creatorAddress, price, version, tokenIds);
        require(_isBundleValid(bundle), "Bundle is not valid");

        // we don't need to check _isBundleTransferrable because `safeTransferFrom` will check that.
        bytes32 hash = _hashToCheck(bundle);
        require(_isBundleSigned(bundle.creatorAddress, hash, v, r, s), "Bundle is not signed");

        IERC721 nonFungibleContract = IERC721(contractAddress);

        for (uint256 i = 0; i < tokenIds.length; i++) {            
            nonFungibleContract.safeTransferFrom(creatorAddress, _msgSender(), tokenIds[i]);
        } 

        _cancelledOrFinalizedBundles[hash] = true;

        Royalty memory royalty = _royalties[creatorAddress];
        IEaselyPayout(payoutContractAddress).splitPayable{ value: price }(creatorAddress, royalty.addresses, royalty.bps);

        // Send back any value greater than the listed price
        payable(_msgSender()).transfer(msgValue - price);

        emit BundleBought(hash);
    }
}"
