    }

    /**
	* Set presale status
	* Setting token presale status for sales
	*/
    function setPresale(bool status) external onlyOwner {
        statusPresale = status;
    }

    /**
    * Get Price
    * Price calculate by referral
    */
    function getPrice(address payable referral) internal view returns (uint256) {
        if(referral != address(0) && referral != msg.sender){
			return tokenPrice.sub(tokenPrice.mul(tokenDiscount).div(100));
        } else {
        	return tokenPrice;
        }
    }

    /**
    * Token Reserve
    * Mint token without any requirement
    */
    function tokenReserve(uint8 number, address owner) public onlyOwner {
        for (uint8 i = 0; i < number; i++) {
			uint256 identity = totalSupply();
            _safeMint(owner, identity);
        }
    }
    
    /**
    * Token Mint
	* If mint status is true, user has enough ether, number below limit, total supply below limit;
	* Mint token, send share to referral
	* Emit minting supply to browser/graph.
    */
    function tokenMint(uint8 number, address payable referral) external payable {
    	require(statusMint == true || statusPresale == true, "token.presale");
        require(statusMint == true || statusPresale == true && presaleAddress[msg.sender] >= number, "token.address");
    	require(statusMint == true || statusPresale == true, "token.mint");
        require(getPrice(referral).mul(number) <= msg.value, "token.price");

        require(tokenMaximum >= number, "token.maximum");
        require(tokenSupply >= totalSupply().add(tokenThreshold).add(number), "token.supply");

		for (uint8 i = 0; i < number; i++) {
			uint256 identity = totalSupply();
            _safeMint(msg.sender, identity);
        }

        if(referral != address(0) && referral != msg.sender){
        	referral.transfer(tokenPrice.mul(number).mul(tokenIncome).div(100));
        	referralAddress[referral] = referralAddress[referral].add(tokenPrice.mul(number).mul(tokenIncome).div(100));
        }

        if(statusMint == false && statusPresale == true){
        	presaleAddress[msg.sender] = presaleAddress[msg.sender].sub(number);
    	}

    	emit TotalSupply({ supply: totalSupply() });
    }
   
}
