    "contracts/mojito/ERC721.sol": {
      "content": "// SPDX-License-Identifier: MIT

/**
 * NOTE: This file is a clone of Zora's clone of the OpenZeppelin ERC721.sol contract from https://github.com/ourzora/core/blob/55b69346b829e88c23b20cdc565123a75fa1339c/contracts/ERC721.sol. See that file to see what changes Zora made to the OpenZeppelin contract.
 *
 * The only change made in this clone is to add `virtual` to `isApprovedForAll` so that we can override it.
 */

pragma solidity ^0.8.0;

import "../openzeppelin/utils/Context.sol";
import "../openzeppelin/token/ERC721/IERC721.sol";
import "../openzeppelin/token/ERC721/extensions/IERC721Metadata.sol";
import "../openzeppelin/token/ERC721/extensions/IERC721Enumerable.sol";
import "../openzeppelin/token/ERC721/IERC721Receiver.sol";
import "../openzeppelin/utils/introspection/ERC165Storage.sol";
import "../openzeppelin/utils/math/SafeMath.sol";
import "../openzeppelin/utils/Address.sol";
import "../openzeppelin/utils/structs/EnumerableSet.sol";
import "../openzeppelin/utils/structs/EnumerableMap.sol";
import "../openzeppelin/utils/Strings.sol";

/**
 * @title ZoraERC721 Non-Fungible Token Standard basic implementation
 * @dev see https://eips.ethereum.org/EIPS/eip-721
 */
contract ERC721 is
    Context,
    ERC165Storage,
    IERC721,
    IERC721Metadata,
    IERC721Enumerable
{
    using SafeMath for uint256;
    using Address for address;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableMap for EnumerableMap.UintToAddressMap;
    using Strings for uint256;

    // Equals to `bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"))`
    // which can be also obtained as `IERC721Receiver(0).onERC721Received.selector`
