// File contracts/FarmAdditions.sol

pragma solidity 0.8.0;
pragma experimental ABIEncoderV2;

abstract contract PkexFarm {
    mapping(address => uint256) public unstakeTime;
    mapping(address => uint256) public rewards;

    function balanceOf(address account) external view virtual returns (uint256);
}

contract PkexFarmAdditional is ReentrancyGuard, Pausable, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    PkexFarm public immutable prevFarm;
    IERC20 public immutable rewardsToken;
    address public immutable dev;
    mapping(address => mapping(uint256 => uint256)) public userRewarded;

    constructor(
        address _prevFarm,
        address _rewardsToken,
        address _dev
    ) {
        require(_prevFarm != address(0), "Prev farm address can't be zero");
        require(_dev != address(0), "Dev wallet can't be zero");
        prevFarm = PkexFarm(_prevFarm);
        rewardsToken = IERC20(_rewardsToken);
        dev = _dev;
    }

    function claim() external whenNotPaused nonReentrant {
        uint256 unstakeTime = prevFarm.unstakeTime(_msgSender());
        require(block.timestamp >= unstakeTime, "still in waiting period");
        require(
            prevFarm.balanceOf(_msgSender()) == 0,
            "You have not unstaked yet"
        );

        uint256 reward = prevFarm.rewards(msg.sender).sub(
            userRewarded[msg.sender][unstakeTime]
        );
        require(reward > 0, "No rewards");
        userRewarded[msg.sender][unstakeTime] = userRewarded[msg.sender][
            unstakeTime
        ].add(reward);

        safeTransferRewardsTokenTo(msg.sender, reward);
        emit RewardPaid(msg.sender, reward);
    }

    function pause() external whenNotPaused onlyOwner {
        _pause();
    }

    function unpause() external whenPaused onlyOwner {
        _unpause();
    }

    function safeTransferRewardsTokenTo(address to, uint256 amount) private {
        uint256 balance = rewardsToken.balanceOf(dev);
        if (balance >= amount) rewardsToken.safeTransferFrom(dev, to, amount);
        else rewardsToken.safeTransferFrom(dev, to, balance);
    }

    event RewardPaid(address indexed user, uint256 reward);
}
