// File: contracts/Claimer.sol


pragma solidity >=0.8.3;


interface IMintContract {
    function mint(uint256 _optionId, address _toAddress) external;

    function ownerOf(uint256 tokenId) external view returns (address owner);
}

/// @title бесплатный минт машин с MusicRacerNft333 на новый контракт
contract Claimer is Ownable {
    uint256 public Count;
    IMintContract public ContractOld =
        IMintContract(0x12e609AcbD92FF7339bB6dDeB87295Fb33A4BaD0);
    IMintContract public ContractNew;

    mapping(uint256 => bool) Transferred;
    event TransferToken(uint256 tokenId);

    function SetOldContract(address addr) public onlyOwner {
        ContractOld = IMintContract(addr);
    }

    function SetNewContract(address addr) public onlyOwner {
        ContractNew = IMintContract(addr);
    }

    /// @dev если истина, то указанный токен уже был использован
    function IsTransferred(uint256 tokenId) public view returns (bool) {
        return Transferred[tokenId];
    }

    /// @dev вызывайте эту функцию, чтобы получить бесплатно машину в новом контракте
    function Transfer(uint256 tokenId) public {
        require(!Transferred[tokenId]);
        Transferred[tokenId] = true;
        ContractNew.mint(1, ContractOld.ownerOf(tokenId));
        ++Count;
        emit TransferToken(tokenId);
    }
}
