contract MBMinter is PaymentSplitter, Ownable {

    uint256 public PRICE = 0.05 ether;
    bool public isAllowListActive = false;
    bool public isPublicSaleActive = false;

    address payable public multisig;

    mapping(address => bool) private _allowList;
    mapping(address => uint256) private _allowListAmount;
    mapping(address => uint256) private _allowListClaimed;

    IMonstersBash nftContract;

    constructor(address _nftContract, address payable _multisig, address[] memory payees, uint256[] memory shares_) PaymentSplitter(payees, shares_) {
        nftContract = IMonstersBash(_nftContract);
        multisig = _multisig;
    }

    function addToAllowList(address[] calldata addresses, uint256[] calldata amounts) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != address(0), "Can't add the null address");

            _allowList[addresses[i]] = true;
            _allowListAmount[addresses[i]] = amounts[i];
            /**
            * @dev We don't want to reset _allowListClaimed count
            * if we try to add someone more than once.
            */
            _allowListClaimed[addresses[i]] = _allowListClaimed[addresses[i]] > 0 ? _allowListClaimed[addresses[i]] : 0;
        }
    }

    function onAllowList(address addr) external view returns (bool) {
        return _allowList[addr];
    }

    function removeFromAllowList(address[] calldata addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != address(0), "Can't add the null address");

            /// @dev We don't want to reset possible _allowListClaimed numbers.
            _allowList[addresses[i]] = false;
            _allowListAmount[addresses[i]] = 0;
        }
    }

    /**
     * @dev We want to be able to distinguish tokens bought during isAllowListActive
     * and tokens bought outside of isAllowListActive
     */
    function allowListClaimedBy(address owner) external view returns (uint256){
        require(owner != address(0), 'Zero address not on Allow List');

        return _allowListClaimed[owner];
    }

    /**
        * @dev How many are left for this address
        */
    function allowListClaimLeft(address owner) external view returns (uint256){
        require(owner != address(0), 'Zero address not on Allow List');

        return _allowListAmount[owner] - _allowListClaimed[owner];
    }

    function purchaseAllowList(uint256 n) external payable {
        require(isAllowListActive, 'Allow List is not active');
        require(!nftContract.saleIsActive(), 'Sale is already active');
        require(_allowList[msg.sender], 'You are not on the Allow List');
        require(_allowListClaimed[msg.sender] + n <= _allowListAmount[msg.sender], 'Purchase exceeds max allowed');

        // turn on sale
        nftContract.toggleSaleState();

        // increase claimed amount
        _allowListClaimed[msg.sender] += n;

        // mint
        uint256 mintIndex = nftContract.totalSupply();
        nftContract.mint(n);
        for (uint256 i = 0; i < n; i++) {
            nftContract.transferFrom(address(this), msg.sender, mintIndex + i);
        }

        // turn off sale
        nftContract.toggleSaleState();
    }

    function mint(uint256 n) external payable {
        require(isPublicSaleActive, 'Public sale is not active');
        require(!nftContract.saleIsActive(), 'OG Sale is already active');
        require(PRICE * n <= msg.value, 'ETH amount is not sufficient');

        // turn on sale
        nftContract.toggleSaleState();

        // mint
        uint256 mintIndex = nftContract.totalSupply();
        nftContract.mint(n);
        for (uint256 i = 0; i < n; i++) {
            nftContract.transferFrom(address(this), msg.sender, mintIndex + i);
        }

        // turn off sale
        nftContract.toggleSaleState();
    }

    function giveaway(address to, uint256 n) external onlyOwner {
        require(!nftContract.saleIsActive(), 'OG Sale is already active');

        // turn on sale
        nftContract.toggleSaleState();

        // mint
        uint256 mintIndex = nftContract.totalSupply();
        nftContract.mint(n);
        for (uint256 i = 0; i < n; i++) {
            nftContract.transferFrom(address(this), to, mintIndex + i);
        }

        // turn off sale
        nftContract.toggleSaleState();
    }

    function setPrice(uint256 _salePrice) external onlyOwner {
        PRICE = _salePrice;
    }

    function setIsAllowListActive(bool _isAllowListActive) external onlyOwner {
        isAllowListActive = _isAllowListActive;
    }

    function setIsPublicActive(bool _isPublicSaleActive) external onlyOwner {
        isPublicSaleActive = _isPublicSaleActive;
    }

    function transferOwnerShipOfNft(address newOwner) external onlyOwner {
        nftContract.transferOwnership(newOwner);
    }

    function withdrawMultisig() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(multisig).transfer(balance);
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external returns (bytes4) {
