// File contracts/FinanceOrder.sol


pragma solidity ^0.8.0;


contract FinanceOrder is Initializable {

    struct Order {
        address creator;
        address token;
        uint tokenId;
        uint price;
        uint expireAt;
    }

    uint public nextOrderId;
    mapping(uint => Order) public orders;

    event OrderMade(address sender, uint orderId, Order order);
    event OrderTaken(address sender, uint orderId);
    event OrderCanceled(address sender, uint orderId);

    function initialize() public initializer {}

    function makeOrder(address token, uint tokenId, uint price, uint expireAt) external {
        IERC721Upgradeable(token).safeTransferFrom(msg.sender, address(this), tokenId);

        Order memory order;
        order.creator = msg.sender;
        order.token = token;
        order.tokenId = tokenId;
        order.price = price;
        order.expireAt = expireAt;

        uint orderId = nextOrderId++;
        orders[orderId] = order;

        emit OrderMade(msg.sender, orderId, order);
    }

    function takeOrder(uint orderId) external payable {
        Order memory order = orders[orderId];
        require(order.price == msg.value);

        IERC721Upgradeable(order.token).safeTransferFrom(address(this), msg.sender, order.tokenId);
        payable(order.creator).transfer(order.price);

        delete orders[orderId];

        emit OrderTaken(msg.sender, orderId);
    }

    function cancelOrder(uint orderId) public {
        Order memory order = orders[orderId];
        IERC721Upgradeable(order.token).safeTransferFrom(address(this), order.creator, order.tokenId);

        emit OrderCanceled(msg.sender, orderId);
    }

    function checkUpkeep(bytes calldata /* checkData */) external returns (bool upkeepNeeded, bytes memory /* performData */) {
        uint expireAt = orders[0].expireAt;
        upkeepNeeded = expireAt != 0 && expireAt < block.timestamp;
    }

    function performUpkeep(bytes calldata /* performData */) external {
        cancelOrder(0);
    }
}
