// File contracts/rabbitars.sol

pragma solidity ^0.8.4;







contract Rabbitars is
  ERC721PausableUpgradeable,
  OwnableUpgradeable,
  UUPSUpgradeable,
  AccessControlEnumerableUpgradeable,
  ReentrancyGuardUpgradeable
{
  using CountersUpgradeable for CountersUpgradeable.Counter;
  using ECDSAUpgradeable for bytes32;

  CountersUpgradeable.Counter internal ids;
  address internal whiteListSigner;

  bytes32 public constant CONTRACT_MANAGER_ROLE = keccak256("CONTRACT_MANAGER_ROLE");
  uint256 public constant PRICE = 195300000000000000;
  uint256 public constant TOTAL_SUPPLY = 11953;
  uint256 public constant FIAT_TOTAL_SUPPLY = 2975;
  // WHITELIST_TOTAL_SUPPLY includes 53 reserve minted Rabbitars 
  uint256 public constant WHITELIST_TOTAL_SUPPLY = 6003;
  uint256 public constant WHITELIST_MAX_MINT = 2;

  mapping(uint256 => bool) internal reservations;
  mapping(address => uint256) internal whitelistMintedAmount;
  bool internal _mintedReserveTokens;
  uint256 internal _publicMintStartsAt;
  uint256 internal _whitelistPartOneMintStartsAt;
  uint256 internal _whitelistPartOneMintEndsAt;
  uint256 internal _whitelistPartTwoMintStartsAt;
  uint256 internal _whitelistPartTwoMintEndsAt;
  string internal _tokenURI;

  event WhiteListSignerSet(address newWhiteListSigner);

  function initialize(
    address managerRoleAddress,
    uint256 publicMintStart,
    uint256 whitelistPartOneMintStartAt,
    uint256 whitelistPartOneMintEndsAt,
    uint256 whitelistPartTwoMintStartAt,
    uint256 whitelistPartTwoMintEndsAt
  ) external initializer {
    __Ownable_init();
    __UUPSUpgradeable_init();
    __ERC721Pausable_init();
    __AccessControlEnumerable_init();
    __ERC721_init("Rabbitars", "PLAY");

    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    _setupRole(CONTRACT_MANAGER_ROLE, _msgSender());
    _setupRole(CONTRACT_MANAGER_ROLE, managerRoleAddress);

    _publicMintStartsAt = publicMintStart;
    _whitelistPartOneMintStartsAt = whitelistPartOneMintStartAt;
    _whitelistPartOneMintEndsAt = whitelistPartOneMintEndsAt;
    _whitelistPartTwoMintStartsAt = whitelistPartTwoMintStartAt;
    _whitelistPartTwoMintEndsAt = whitelistPartTwoMintEndsAt;

    _mintedReserveTokens = false;
  }

  function version() external pure virtual returns (string memory) {
    return "1.0.1";
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControlEnumerableUpgradeable, ERC721Upgradeable)
    returns (bool)
  {
    return
      interfaceId == type(IERC721Upgradeable).interfaceId ||
      super.supportsInterface(interfaceId);
  }

  function setBaseURI(string memory baseURI)
  external
  onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _tokenURI = baseURI;
  }

  function _baseURI()
  internal
  view
  virtual
  override
  returns (string memory)
  {
    return _tokenURI;
  }

  function setPublicMintStartsAt(uint256 timestamp)
    external
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _publicMintStartsAt = timestamp;
  }

  function getPublicMintStartsAt()
    external
    view
    onlyRole(CONTRACT_MANAGER_ROLE)
    returns (uint256)
  {
    return _publicMintStartsAt;
  }

  function setWhitelistPartOneMintStartsAt(uint256 timestamp)
    external
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _whitelistPartOneMintStartsAt = timestamp;
  }

  function getWhitelistPartOneMintStartsAt()
    external
    view
    onlyRole(CONTRACT_MANAGER_ROLE)
    returns (uint256)
  {
    return _whitelistPartOneMintStartsAt;
  }

  function setWhitelistPartOneMintEndsAt(uint256 timestamp)
    external
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _whitelistPartOneMintEndsAt = timestamp;
  }

  function getWhitelistPartOneMintEndsAt()
    external
    view
    onlyRole(CONTRACT_MANAGER_ROLE)
    returns (uint256)
  {
    return _whitelistPartOneMintEndsAt;
  }

  function setWhitelistPartTwoMintStartsAt(uint256 timestamp)
    external
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _whitelistPartTwoMintStartsAt = timestamp;
  }

  function getWhitelistPartTwoMintStartsAt()
    external
    view
    onlyRole(CONTRACT_MANAGER_ROLE)
    returns (uint256)
  {
    return _whitelistPartTwoMintStartsAt;
  }

  function setWhitelistPartTwoMintEndsAt(uint256 timestamp)
    external
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    _whitelistPartTwoMintEndsAt = timestamp;
  }

  function getWhitelistPartTwoMintEndsAt()
    external
    view
    onlyRole(CONTRACT_MANAGER_ROLE)
    returns (uint256)
  {
    return _whitelistPartTwoMintEndsAt;
  }

  function mintTokens(address account, uint256 amount) internal virtual {
    require(
      (ids.current() + amount) <= TOTAL_SUPPLY,
      "Maximum minted token supply"
    );

    for (uint256 i = 0; i < amount; i++) {
      _safeMint(account, ids.current());
      ids.increment();
    }
  }

  function whitelistPartOneMintTokens(uint256 amount, bytes memory signature)
    public
    payable
    virtual
    nonReentrant
  {
    require(
      _canMint(msg.sender, amount, 1, signature),
      "Must be signed by white list signer"
    );
    require(
      amount <= WHITELIST_MAX_MINT,
      "Over the maximum amount allowed to mint at once"
    );
    require(
      block.timestamp >= _whitelistPartOneMintStartsAt,
      "Whitelist minting has not started yet"
    );
    require(
      block.timestamp <= _whitelistPartOneMintEndsAt,
      "Minting has closed, sorry!"
    );
    require(
      (whitelistMintedAmount[msg.sender] + amount) <= WHITELIST_MAX_MINT,
      "Over the maximum amount allowed to mint"
    );
    uint256 totalPrice = (amount * PRICE);
    require(msg.value >= totalPrice, "Insufficent eth sent");

    mintTokens(msg.sender, amount);
    whitelistMintedAmount[msg.sender] =
      whitelistMintedAmount[msg.sender] +
      amount;
  }

  function whitelistPartTwoMintTokens(uint256 amount, bytes memory signature)
    public
    payable
    virtual
    nonReentrant
  {
    require(
      _canMint(msg.sender, amount, 2, signature),
      "Must be signed by white list signer"
    );
    require(
      amount <= WHITELIST_MAX_MINT,
      "Over the maximum amount allowed to mint at once"
    );
    require(
      (ids.current() + amount) <= WHITELIST_TOTAL_SUPPLY,
      "Minted over the maximum token supply"
    );
    require(
      block.timestamp >= _whitelistPartTwoMintStartsAt,
      "Whitelist minting has not started yet"
    );
    require(
      block.timestamp <= _whitelistPartTwoMintEndsAt,
      "Minting has closed, sorry!"
    );
    require(
      (whitelistMintedAmount[msg.sender] + amount) <= WHITELIST_MAX_MINT,
      "Over the maximum amount allowed to mint"
    );
    uint256 totalPrice = (amount * PRICE);
    require(msg.value >= totalPrice, "Insufficent eth sent");

    mintTokens(msg.sender, amount);
    whitelistMintedAmount[msg.sender] =
      whitelistMintedAmount[msg.sender] +
      amount;
  }

  function publicMintTokens(
    uint256 amount,
    uint256 reservationId,
    bytes memory signature
  ) public payable virtual nonReentrant {
    require(
      _canMint(msg.sender, amount, reservationId, signature),
      "Must be signed by white list signer"
    );
    require(
      block.timestamp >= _publicMintStartsAt,
      "Public minting has not started yet"
    );
    require(
      (ids.current() + amount) <= TOTAL_SUPPLY,
      "Minted over the maximum token supply"
    );
    require(amount <= 5, "Minted over maximum amount per transaction");

    uint256 totalPrice = (amount * PRICE);
    require(msg.value >= totalPrice, "Insufficient eth sent");

    require(reservations[reservationId] == false, "Reservation already used");

    mintTokens(msg.sender, amount);

    reservations[reservationId] = true;
  }

  function mintReserveTokens(address account, uint256 reservedAmount)
    external
    virtual
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(_mintedReserveTokens == false, "Already minted reserve tokens");

    mintTokens(account, reservedAmount);

    _mintedReserveTokens = true;
  }

  function fiatPostMint(address[] memory accounts, uint256[] memory amounts)
    external
    virtual
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(
      accounts.length == amounts.length,
      "Incorrect length match for accounts and amounts"
    );

    uint256 totalFiatAmount = 0;

    for (uint256 i = 0; i < amounts.length; i++) {
      totalFiatAmount += amounts[i];
    }

    require(
      totalFiatAmount <= FIAT_TOTAL_SUPPLY,
      "Attempted to mint over the fiat supply cap"
    );

    require(
      (ids.current() + totalFiatAmount) <= TOTAL_SUPPLY,
      "Minting over max total supply"
    );

    for (uint256 i = 0; i < accounts.length; i++) {
      mintTokens(accounts[i], amounts[i]);
    }
  }

  function _authorizeUpgrade(address newImplementation)
    internal
    virtual
    override
    onlyRole(CONTRACT_MANAGER_ROLE)
  {}

  function getWhiteListSigner() external view virtual returns (address) {
    return whiteListSigner;
  }

  function setWhiteListSigner(address newWhiteListSigner)
    public
    virtual
    onlyRole(CONTRACT_MANAGER_ROLE)
  {
    whiteListSigner = newWhiteListSigner;

    emit WhiteListSignerSet(whiteListSigner);
  }

  function _getSigner(bytes32 hash, bytes memory signature)
    internal
    pure
    returns (address)
  {
    return hash.toEthSignedMessageHash().recover(signature);
  }

  function _verifyWhiteListSignature(
    address account,
    uint256 amount,
    uint256 reservationId,
    bytes memory signature,
    address signer
  ) internal pure returns (bool) {
    return
      _getSigner(
        keccak256(
          abi.encodePacked(account, amount, reservationId)
        ),
        signature
      ) == signer;
  }

  function _canMint(
    address wallet,
    uint256 amount,
    uint256 reservationId,
    bytes memory signature
  ) internal view returns (bool) {
    return
      _verifyWhiteListSignature(wallet, amount, reservationId, signature, whiteListSigner);
  }

  function withdraw()
  public onlyRole(CONTRACT_MANAGER_ROLE)
  {
    uint256 balance = address(this).balance;
    payable(msg.sender).transfer(balance);
  }
}


