pragma solidity ^0.8.1;









/**
 * @title World of Warcats
 */
contract Warcat is
    ContextMixin,
    NativeMetaTransaction,
    Ownable,
    RandomlyAssigned
{
    using SafeMath for uint256;

    string public baseTokenURI = "";
    string public baseExtension = ".json";
    string public notRevealedURI = "";

    uint256 MAX_SUPPLY = 1000;
    uint256 public totalMint = 0;

    bool public revealed = false;
    bool public publicSale = false;

    // address of the payment contract (for payment splitter)
    address payable public payment;

    // For RandomlyAssigned: Max supply is 1000; id start from 1 (instead of 0)
    constructor(
        string memory _name,
        string memory _symbol,
        string memory _uri,
        string memory _notRevealedURI,
        address _payment
    ) ERC721(_name, _symbol) RandomlyAssigned(1000, 1) {
        baseTokenURI = _uri;
        notRevealedURI = _notRevealedURI;
        payment = payable(_payment);
        _initializeEIP712(_name);
        _mintList(10);
    }

    /**
     * @dev You can mint 5 Warcats per transaction
     */
    function mint(uint8 _num) external payable {
        require(
            _num <= 5,
            "Warcat: You can only mint 5 per transaction!"
        );
        require(
            publicSale,
            "Warcat: Mint has not started yet."
        );
        require(
            totalMint + uint256(_num) <= MAX_SUPPLY,
            "Warcat: Sorry, all Warcats have been sold."
        );
        require(
            msg.value >= uint256(_num) * 666e14,
            "Warcat: You need to pay at least the required cost."
        );
        _mintList(_num);
    }

    /**
     * @dev Burn or mint the remaining tokens (make sure publicSale has closed)
     */
    function burnRemaining(uint256 _num, bool _mintOnly) external onlyOwner {
        require(
            !publicSale,
            "Warcat: Cannot burn until public sale has ended."
        );
        require(
            totalMint + _num <= MAX_SUPPLY,
            "Warcat: No Warcats remaining."
        );
        for (uint8 i = 0; i < _num; i++) {
            // randomly pick a new token id (that has not been minted)
            uint256 newTokenId = nextToken();
            require(
                !_exists(newTokenId),
                "Warcat: One of the tokens already got minted by someone else..."
            );
            _safeMint(msg.sender, newTokenId);
            if (!_mintOnly){
                _burn(newTokenId);
            }
        }
    }

    /**
     * @dev mint _num of Warcats
     * @param _num Quantity to mint
     */
    function _mintList(uint8 _num) private {
        for (uint8 i = 0; i < _num; i++) {
            // randomly pick a new token id (that has not been minted)
            uint256 newTokenId = nextToken();
            require(
                !_exists(newTokenId),
                "Warcat: One of the tokens already got minted by someone else..."
            );
            _safeMint(msg.sender, newTokenId);
        }
        totalMint += uint256(_num);
    }

    /**
     * @dev set revealed to true (only Owner)
     */
    function reveal() external onlyOwner {
        revealed = true;
    }

    /**
     * @dev change the notRevealedURI (only Owner)
     * @param _notRevealedURI Not revealed URI
     */
    function setNotRevealedURI(string memory _notRevealedURI)
        external
        onlyOwner
    {
        notRevealedURI = _notRevealedURI;
    }

    /**
     * @dev change the baseTokenURI (only Owner)
     * @param _baseTokenURI base token URI
     */
    function setBaseURI(string memory _baseTokenURI) external onlyOwner {
        baseTokenURI = _baseTokenURI;
    }

    /**
     * @dev returns the token URI of _tokenId
     * @param _tokenId id of the token
     */
    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(_exists(_tokenId), "Warcat: This token does not exist!");
        if (revealed == false) {
            return notRevealedURI;
        }
        return
            string(
                abi.encodePacked(
                    baseTokenURI,
                    Strings.toString(_tokenId),
                    baseExtension
                )
            );
    }

    /**
     * @dev this is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
     */
    function _msgSender() internal view override returns (address sender) {
        return ContextMixin.msgSender();
    }

    /**
     * @dev withdraw all balance of this contract to the Payment contract (only Owner)
     */
    function withdraw() external payable onlyOwner {
        (bool success, ) = payable(payment).call{value: address(this).balance}(
            ""
        );
        require(success, "Transfer failed.");
    }

    /**
     * @dev set the base extension of the metadata file (only Owner)
     * @param _newBaseExtension extension (empty or with a dot infront)
     */
    function setBaseExtension(string memory _newBaseExtension)
        public
        onlyOwner
    {
        baseExtension = _newBaseExtension;
    }

    /**
     * @dev get the ids of the Warcats owned by _owner
     * @param _owner address
     */
    function walletOfOwner(address _owner)
        external
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    /**
     * @dev start or stop the pledge function and the claim function for pubilc sale (only Owner)
     * @param _state true means start
     */
    function publicSaleStart(bool _state) external onlyOwner {
        publicSale = _state;
    }

    /**
     * @dev update Payment address in case there is a problem with the original one
     * @param _payment updated payment address
     */
    function updatePayment(address payable _payment) external onlyOwner {
        payment = _payment;
    }

}
