    require(givenAwayPumpkins + _numTokens <= GIVEAWAY_POOL, "Pumpkinheads: Not enough tokens left!");
    _mintPumpkins(_receiver, _numTokens, givenAwayPumpkins);
    givenAwayPumpkins += _numTokens;
  }

