// File: contracts/SeedToken.sol


pragma solidity >=0.4.22 <0.9.0;




contract SeedToken is ERC20Upgradeable, AccessControlUpgradeable {
    // Constants
    uint public constant DECIMALS = 18;
    uint public constant SECONDS_IN_A_DAY = 86400;
    uint public constant INITIAL_ALLOTMENT = 10 * (10 ** DECIMALS);
    uint public constant BASE_EMISSION_PER_DAY = 10 * (10 ** DECIMALS);
    uint public constant BASE_EMISSION_LIMIT = 1200 * (10 ** DECIMALS);
    uint public constant MAX_LEVEL = 30;

    // Public variables
    uint public _emissionStart;
    bool public _isClaimingActive;
    mapping(uint => uint) public alreadyClaimed;
    mapping(uint => uint) private _lastClaim;
    mapping(uint => uint) private _pumpkinLevels;

    address private _pumpkinheadsAddress;
    bytes32 public constant SPENDER_ROLE = keccak256("SPENDER_ROLE");
    bytes32 public constant MINTER_ROLE  = keccak256("MINTER_ROLE");

    event PumpkinsUpgraded(uint[] indexed tokenIds, uint[] indexed levels);
    event Harvested(address indexed user, uint[] indexed tokenIds, uint amount);

    function initialize(
      string memory name,
      string memory symbol,
      uint emissionStartTimestamp,
      bool isClaimingActive
    ) public initializer
    {
      __AccessControl_init();
      __ERC20_init_unchained(name, symbol);
      _emissionStart = emissionStartTimestamp;
      _isClaimingActive = isClaimingActive;
      _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function setIsClaimingActive(bool isActive) public onlyRole(DEFAULT_ADMIN_ROLE) {
      _isClaimingActive = isActive;
    }

    /**
     * @dev When accumulated Seeds have last been claimed for a Pumpkin index
     */
    function lastClaim(uint tokenIndex) public view returns (uint) {
        require(Pumpkins(_pumpkinheadsAddress).exists(tokenIndex), "NFT at index has not been minted yet");
        return _lastClaimedAt(tokenIndex);
    }

    /**
     * @dev Accumulated Seed tokens for a Pumpkin token index.
     */
    function accumulated(uint tokenIndex) public view returns (uint) {
        require(block.timestamp > _emissionStart, "Emission has not started yet");
        require(Pumpkins(_pumpkinheadsAddress).exists(tokenIndex), "NFT at index has not been minted yet");
        return _accumulated(tokenIndex);
    }

    function accumulatedForUser(address _user) public view returns (uint) {
      require(block.timestamp > _emissionStart, "Emission has not started yet");

      // not checking whether token exists, since it is expensive
      uint[] memory pumpkins = Pumpkins(_pumpkinheadsAddress).pumpkinsOfOwner(_user);
      uint totalAccumulated = 0;
      for (uint i = 0; i < pumpkins.length; i++) {
        totalAccumulated += _accumulated(pumpkins[i]);
      }

      return totalAccumulated;
    }

    /**
     * @dev Permissioning not added because it is only callable once. It is set right after deployment and verified.
     */
    function setPumpkinheadsAddress(address pumpkinheadsAddress) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_pumpkinheadsAddress == address(0), "Already set");

        _pumpkinheadsAddress = pumpkinheadsAddress;
    }

    /**
     * @dev Claim mints Seeds and supports multiple Pumpkin token indices at once.
     */
    function claim(uint[] memory tokenIndices) public returns (uint) {
        require(_isClaimingActive, "Claiming is not active yet");
        require(Pumpkins(_pumpkinheadsAddress).ownsAllTokens(msg.sender, tokenIndices), "Sender does not own all tokens");

        uint totalClaimQty = 0;
        for (uint i = 0; i < tokenIndices.length; i++) {
            uint tokenIndex = tokenIndices[i];
            uint claimQty = _accumulated(tokenIndex);
            if (claimQty != 0) {
                totalClaimQty = totalClaimQty + claimQty;
                _lastClaim[tokenIndex] = block.timestamp;
                alreadyClaimed[tokenIndex] += claimQty;
            }
        }

        if (totalClaimQty != 0) {
          _mint(msg.sender, totalClaimQty);
          emit Harvested(msg.sender, tokenIndices, totalClaimQty);
        }

        return totalClaimQty;
    }

    function claimForUser() public returns (uint) {
      uint[] memory pumpkins = Pumpkins(_pumpkinheadsAddress).pumpkinsOfOwner(_msgSender());
      return claim(pumpkins);
    }

    function pumpkinLevel(uint _tokenIndex) public view returns (uint) {
      require(Pumpkins(_pumpkinheadsAddress).exists(_tokenIndex), "NFT at index has not been minted yet");
      return _pumpkinLevel(_tokenIndex);
    }

    function upgradePumpkins(
      uint[] memory _tokenIndices,
      uint[] memory _levels,
      bool _claimAll
    ) public {
      require(_tokenIndices.length == _levels.length, "Pumpkinheads: Invalid parameters sent!");
      // if user wants to harvest and upgrade in one transaction
      if (_claimAll) {
        claimForUser();
      } else {
        // else harvest only those pumpkins that will be upgraded
        claim(_tokenIndices);
      }

      uint totalUpgradeCost = 0;
      for (uint i = 0; i < _tokenIndices.length; i++) {
        totalUpgradeCost += _upgradeCost(_tokenIndices[i], _levels[i]);
      }

      require(balanceOf(msg.sender) >= totalUpgradeCost, "Pumpkinheads: Insufficient balance");

      for (uint i = 0; i < _tokenIndices.length; i++) {
        _pumpkinLevels[_tokenIndices[i]] = _levels[i];
        alreadyClaimed[_tokenIndices[i]] = 0;
      }

      burn(totalUpgradeCost);
      emit PumpkinsUpgraded(_tokenIndices, _levels);
    }

    function upgradeCost(uint _index, uint _level) public view returns (uint) {
      require(
        Pumpkins(_pumpkinheadsAddress).exists(_index),
        "NFT at index has not been minted yet"
      );

      return _upgradeCost(_index, _level);
    }

    /**
     * @dev Burns a quantity of tokens held by the caller.
     *
     * Emits an {Transfer} event to 0 address
     *
     */
    function burn(uint burnQuantity) public returns (bool) {
        _burn(msg.sender, burnQuantity);
        return true;
    }

    /**
     * @dev For future contracts in the Pumpkinheads world to interact with
     * the Seed contract.
     */
    function spendFor(address user, uint amount) public onlyRole(SPENDER_ROLE) returns (bool) {
      _burn(user, amount);
      return true;
    }

    function mintFor(address user, uint amount) public onlyRole(MINTER_ROLE) returns (bool) {
      _mint(user, amount);
      return true;
    }

    function remainingToClaim(uint tokenIndex) public view returns(uint) {
      uint level = pumpkinLevel(tokenIndex);
      return (seedEmissionLimit(level) - alreadyClaimed[tokenIndex]);
    }

    function _lastClaimedAt(uint tokenIndex) internal view returns (uint) {
      uint lastClaimed = uint(_lastClaim[tokenIndex]) != 0 ? uint(_lastClaim[tokenIndex]) : _emissionStart;
      return lastClaimed;
    }

    function _accumulated(uint tokenIndex) internal view returns (uint) {
      uint lastClaimed = _lastClaimedAt(tokenIndex);
      uint accumulationPeriod = block.timestamp;
      uint level = _pumpkinLevel(tokenIndex);
      uint totalAccumulated = (accumulationPeriod - lastClaimed) * (_pumpkinSeedEmissionPerDay(level) / SECONDS_IN_A_DAY);

      // If claim hasn't been done before for the index, add initial allotment (plus prereveal multiplier if applicable)
      if (lastClaimed == _emissionStart) {
          uint initialAllotment = INITIAL_ALLOTMENT;
          totalAccumulated = totalAccumulated + initialAllotment;
      }

      uint availableToClaim = seedEmissionLimit(level) - alreadyClaimed[tokenIndex];
      if (totalAccumulated < availableToClaim) {
        return totalAccumulated;
      } else {
        return availableToClaim;
      }
    }

    function _upgradeCost(uint _index, uint _targetLevel) internal view returns (uint) {
      uint currentLevel = _pumpkinLevel(_index);

      require(currentLevel < _targetLevel, "Target level lower or equal than current level");
      uint total = 0;
      for (uint i = currentLevel + 1; i <= _targetLevel; i++) {
        total += _levelCost(i);
      }

      return total;
    }

    // default level is 1, but default solidity value is 0
    function _pumpkinLevel(uint _tokenIndex) internal view returns (uint) {
      uint level = _pumpkinLevels[_tokenIndex];
      if (level == 0) {
        return 1;
      } else {
        return level;
      }
    }

    function _levelCost(uint _level) public pure returns (uint) {
      require(_level <= MAX_LEVEL, "Max level already reached");
      return _pumpkinSeedEmissionPerDay(_level - 1) *  _level;
    }

    function _pumpkinSeedEmissionPerDay(uint _level) public pure returns (uint) {
      require(_level <= MAX_LEVEL, "Max level already reached");
      return BASE_EMISSION_PER_DAY * _level;
    }

    function seedEmissionLimit(uint _level) public pure returns(uint) {
      require(_level <= MAX_LEVEL, "Max level already reached");
      return BASE_EMISSION_LIMIT * _level;
    }
}
