        bytes32[] memory hashes = new bytes32[](n);

        for (uint256 i = 0; i < n; ++i)
        {
            hashes[i] = _tokenHashes[i];
        }

        return hashes;
    }

    function getAuctionInfo() public view returns(
        bool hasStarted,
        bool hasEnded,
        uint256 blocksUntilAuctionStart,
        uint256 currentPrice,
        uint256 blocksUntilPriceDrop,
        uint256 nextPrice,
        uint256 currentInvocationCount,
        uint256 maxInvocationCount)
    {
        if (_auctionStartBlock == 0)
        {
            hasStarted = false;
            hasEnded = false;
        }

        uint256 blockNumber = block.number;
        if (blockNumber >= _auctionStartBlock)
        {
            hasStarted = _auctionStartBlock != 0;

            blocksUntilAuctionStart = 0;
        }
        else
        {
            hasStarted = false;

            blocksUntilAuctionStart = _auctionStartBlock - blockNumber;
        }

        if (_auctionEndBlock == 0)
        {
            hasEnded = false;

            (currentPrice, 
            blocksUntilPriceDrop, 
            nextPrice) = _getPriceInfo(blockNumber);
        }
        else
        {
            hasEnded = true;

            (currentPrice, 
            , 
            nextPrice) = _getPriceInfo(_auctionEndBlock);
            blocksUntilPriceDrop = 0;
        }

        currentInvocationCount = totalSupply();
        maxInvocationCount = _maxInvocationCount;
    }

    function purchase() 
        public payable nonReentrant returns (uint256 tokenId)
    {
        require(_auctionStartBlock > 0 && block.number >= _auctionStartBlock, 
                "auction not started");

        uint256 totalInvocations = totalSupply();
        uint256 price;

        if (_auctionEndBlock == 0)
        {
            (price, , ) = _getPriceInfo(block.number);

            uint256 normalInvocationsRemaining = _maxInvocationCount - totalInvocations - _totalWhitelistedMintBudget;

            if (normalInvocationsRemaining == 1)
            {
                _auctionEndBlock = block.number;
            }
        }
        else
        {
            // see if they're whitelisted for any free mints
            uint256 whitelistIndex;
            uint256 whitelistMintsUsed;
            uint256 whitelistMintBudget;
            for (uint256 i = 0; i < _whitelistedMinters.length; ++i)
            {
                (address whitelistAddress,
                uint256 mintsUsed,
                uint256 mintBudget) = _decodeWhitelistedMinter(_whitelistedMinters[i]);
                
                if (whitelistAddress == msg.sender)
                {
                    whitelistIndex = i;
                    whitelistMintsUsed = mintsUsed;
                    whitelistMintBudget = mintBudget;
                    break;
                }
            }

            if (whitelistMintsUsed < whitelistMintBudget)
            {
                // this is a whitelisted free mint
                _whitelistedMinters[whitelistIndex] = _encodeWhitelistedMinter(
                    msg.sender,
                    whitelistMintsUsed + 1,
                    whitelistMintBudget);
            }
            else
            {
                revert("auction has ended");
            }
        }

        require(msg.value >= price, "insufficient eth sent for purchase");

        // send fee on
        if (price > 0)
        {
            (bool success, ) = _recipient.call{value:price}("");
            require(success, "payment failed");
        }

        // return any extra eth if they overpaid, for example because the
        // transaction was mined after a price drop
        uint256 extraEth = msg.value - price;
        if (extraEth > 0)
        {
            (bool success, ) = msg.sender.call{value:extraEth}("");
            require(success, "refund failed");
        }

        tokenId = totalInvocations;
        bytes32 hash = _computeTokenHash(tokenId);
        
        _tokenHashes[tokenId] = hash;
        _safeMint(msg.sender, tokenId);
        
        return tokenId;
    }

    function getTokenHtml(uint256 tokenId) public view returns(string memory)
    {
        return string(abi.encodePacked(
            _htmlPrefix, 
            uint256(_tokenHashes[tokenId]).toHexString(), 
            _htmlPostfix));
    }

    function canMintWhitelisted() public view returns(bool)
    {
        for (uint256 i = 0; i < _whitelistedMinters.length; ++i)
        {
            (address whitelistAddress,
            uint256 mintsUsed,
            uint256 mintBudget) = _decodeWhitelistedMinter(_whitelistedMinters[i]);
            
            if (whitelistAddress == msg.sender)
            {
                return mintsUsed < mintBudget;
            }
        }

        return false;
    }

    function _computeTokenHash(uint256 tokenId) private view returns(bytes32)
    {
        return keccak256(abi.encodePacked(
            "Fusion.TokenHash", 
            tokenId,
            block.number,
            tokenId > 0 ? _tokenHashes[tokenId - 1] : bytes32(0)));
    }

    function _getPriceInfo(uint256 blockNumber) private view returns (
        uint256 currentPrice, 
        uint256 blocksUntilPriceDrop, 
        uint256 nextPrice)
    {
        if (_auctionStartBlock == 0)
        {
            return (_priceTier[0], 0, _priceTier[0] - _priceDropAmount);
        }
        
        if (blockNumber < _auctionStartBlock)
        {
            uint256 firstDrop = _auctionStartBlock + _priceTierDropInterval[0];
            return (_priceTier[0], 
                firstDrop - blockNumber, 
                _priceTier[0] - _priceDropAmount);
        }

        uint256 blocksSinceStart = blockNumber - _auctionStartBlock;

        uint256 tier = _priceTier.length - 1;
        while (blocksSinceStart < _priceTierStart[tier])
        {
            --tier;
        }

        uint256 blocksSinceTierStart = blocksSinceStart - _priceTierStart[tier];
        uint256 dropCount = blocksSinceTierStart / _priceTierDropInterval[tier];
        uint256 priceDrop = dropCount * _priceDropAmount;

        if (priceDrop >= (_priceTier[tier] - _minPrice))
        {
            // end!
            return (_minPrice, 0, _minPrice);
        }

        uint256 price = _priceTier[tier] - priceDrop;
        uint256 nextDrop = (dropCount + 1) * _priceTierDropInterval[tier];

        return (price, 
            nextDrop - blocksSinceTierStart, 
            price - _priceDropAmount);
    }

    function _encodeWhitelistedMinter(
        address whitelistedAddress, 
        uint256 mintsUsed, 
        uint256 budget)
        private pure returns (uint256)
    {
        return (budget << (26 * 8)) | 
                (mintsUsed << (20 * 8)) | 
                uint160(whitelistedAddress);
    }

    function _decodeWhitelistedMinter(uint256 encoded)
        private pure returns (
            address whitelistedAddress, 
            uint256 mintsUsed, 
            uint256 budget
        )
    {
        return (address(uint160(encoded)),
                (encoded >> (20 * 8)) & 0xffffffffffff,
                encoded >> (26 * 8));
    }
}
