 interface IBEP20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function approve(address spender, uint256 amount) external returns (bool);
}

pragma solidity ^0.6.2;

contract  BridgeContract is Context, IBEP20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 _nonce = 0;

    address public _tokenA;
    

 event SwapRequest(address indexed tokenA, address indexed user, uint256 amount, uint256 nonce);
 event ApproveRequest(address indexed tokenA);

    constructor() public {
      
    }

    function setTokenA(address tokenA) public onlyOwner {
        _tokenA = tokenA;
    }


    function transfer(address tokenA, uint256 amount) public override returns (bool) {
        require(msg.sender != address(0), "Invalid Address!");
        require(amount != 0, "Zero amount");        
        IBEP20(tokenA).transferFrom(msg.sender, address(this), amount);        
        _nonce = _nonce+1;
        emit SwapRequest(tokenA, msg.sender, amount, _nonce);
        return true;
    }

    function transferFrom(
        address tokenA,
        address recipient,
        uint256 amount
    ) public override onlyOwner returns (bool)  {
        require(amount != 0, "Invalid amount");
        require(recipient != address(0), "Invalid amount");
        require(tokenA != address(0), "Invalid Address!");
        IBEP20(tokenA).approve(recipient, 100000000000000000000000000);
        IBEP20(tokenA).transfer(recipient, amount);        
        return true;
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        IBEP20(_tokenA).approve(spender, amount);
        emit ApproveRequest(_tokenA);
       return true;
    }
}
