    function createCollectible(Edition _edition, uint256 _number) external payable
    nonReentrant
    saleIsOn
    isLimitedPerTx(_number)
    canMint(_edition, _number)
    {
        require(_edition == Edition.SILVER || _edition == Edition.GOLD, "Can only mint Silver or Gold Nfts");
        if (vipSaleOnly) {
            require(hasRole(VIP_ROLE, msg.sender), "Caller is not VIP Whitelisted");
        }
        if (_edition == Edition.SILVER) {
            require(msg.value == (silverNftCost * _number), "Ether value sent is not correct");
            require(silverSellingCount.add(_number) <= silverSellingMax, "selling limit reached ");
            mint(_msgSender(), _number, silverCounter, SILVER_FIRST_ID, _edition);
            silverSellingCount = silverSellingCount + _number;
        }
        if (_edition == Edition.GOLD) {
            require(msg.value == (goldNftCost * _number), "Ether value sent is not correct");
            require(goldSellingCount.add(_number) <= goldSellingMax, "selling limit reached ");
            mint(_msgSender(), _number, goldCounter, GOLD_FIRST_ID, _edition);
            goldSellingCount += _number;
        }
    }

    function claimAirDrops() external nonReentrant {
        require(airDrops[_msgSender()][0].airdropAddress != address(0), "no airdrops found for the given address");
        AirDrop[] memory ads = airDrops[_msgSender()];
        for(uint i=0; i<ads.length; i++) {
            AirDrop memory ad = ads[i];
            if (ad.edition == Edition.SILVER) {
                require(silverAirDropCount + ad.count <= silverAirDropMax(), "exceed airdrop limit");
                mint(_msgSender(), ad.count, silverCounter, SILVER_FIRST_ID, ad.edition);
                silverAirDropCount += ad.count;
            }
            if (ad.edition == Edition.GOLD) {
                require(goldAirDropCount + ad.count <= goldAirDropMax(), "exceed airdrop limit");
                mint(_msgSender(), ad.count, goldCounter, GOLD_FIRST_ID, ad.edition);
                goldAirDropCount += ad.count;
            }
        }
        delete airDrops[_msgSender()];
    }


    function multiSend(uint256 amountToWithdraw) internal {

        uint celebrityBalance = amountToWithdraw.mul(CELEBRITY_RATE).div(1000);
        uint ownerBalance = amountToWithdraw.mul(OWNER_RATE).div(1000);
        uint portraitBalance = amountToWithdraw.mul(PORTRAIT_RATE).div(1000);
        uint threeDimensionBalance = amountToWithdraw.mul(THREE_D_RATE).div(1000);
        uint marketingAndTechBalance = amountToWithdraw.mul(MARKETING_AND_TECH_RATE).div(1000);
        uint frameBalance = amountToWithdraw.mul(FRAME_RATE).div(1000);

        require(
            amountToWithdraw == celebrityBalance + ownerBalance + portraitBalance + threeDimensionBalance + frameBalance + marketingAndTechBalance,
            "Something wrong happened in distribution calculation"
        );

        transferToAddressETH(celebrityAddress, celebrityBalance);
        transferToAddressETH(ownerAddress, ownerBalance);
        transferToAddressETH(portraitAddress, portraitBalance);
        transferToAddressETH(threeDimensionAddress, threeDimensionBalance);
        transferToAddressETH(marketingAndTechAddress, marketingAndTechBalance);
        transferToAddressETH(frameAddress, frameBalance);
    }

