    /*
      minting number cannot exceed 255
    */
    function mint(address to, uint256 _number, Counters.Counter storage counter, uint256 _tokenFirstId, Edition edition) internal returns (bool)
    {
        require(_number > 0, "You need to indicate a number of nft to mint greater than 0");
        for (uint256 i= 0; i < _number; i++)
        {
            uint256 tokenID = counter.current() + _tokenFirstId;
            _safeMint(to, tokenID);
            counter.increment();
        }
        emit NftMinted(_msgSender(), to, edition, _number);

        return true;
    }

