        uint256 _amount = address(this).balance;
        require(_amount > 0, "No ETH to Withdraw");
        multiSend(_amount);

        emit WithdrawnToEntities(_msgSender(), _amount);
    }

    /*
      Mint to the special address, only the owner can do,
      Airdrop the nft to the given address
      minting number cannot exceed 255
    */
    function mintByOwner(address _airDropAddress, Edition _edition, uint256 _number) external
    onlyOwner
    canMint(_edition, _number)
    {
        require(_number <= 255, "exceed the max minting limit per time");
        if (_edition == Edition.DIAMOND) {
            mint(_airDropAddress, _number,diamondCounter, DIAMOND_FIRST_ID, _edition);
        } else if (_edition == Edition.BLACK) {
            mint(_airDropAddress, _number, blackCounter, BLACK_FIRST_ID, _edition);
        } else if (_edition == Edition.GOLD) {
            mint(_airDropAddress, _number, goldCounter, GOLD_FIRST_ID, _edition);
        } else if (_edition == Edition.SILVER) {
            mint(_airDropAddress, _number, silverCounter, SILVER_FIRST_ID, _edition);
        }

    }

