pragma solidity ^0.8.0;



contract LORE is ReentrancyGuard, ERC20 ("LORE","LORE") {
    uint256 public RATE = 1 ether; //1 LORE per day
    string [] private _whitepaper; 
    uint256 constant public DISTRIBUTION_PERIOD = 86400; //86400 seconds = 1 day
    uint256 constant public DURATION = 100 * 31556926; //100 years * 31556926 seconds per year
    uint256 constant public START = 1630086303; //Deployment of Loot contract
    uint256 constant public END = START + DURATION;

	mapping (uint256 => uint256) public cumulativeClaimedByTokenId;

    address public LootAddress;
	IERC721Enumerable public Loot = IERC721Enumerable(0xFF9C1b15B16263C61d017ee9F65C50e4AE0113D7);

	constructor() public { 
        _whitepaper=['~ 100 Years of LORE ~', 
            '', 
            'LORE is', 
            'a universe forging token', 
            '', 
            'LORE is for use with Loot', 
            'and all other open-ended permissionless ecosystems&#65292;', 
            'released in the spirit of Loot and Satoshi', 
            '', 
            'The LORE Foundation has taken 0 premine or cut', 
            '', 
            'A fixed issuance & known supply&#65292;', 
            'LORE is backed by Math', 
            'with no further human governance', 
            '', 
            '1 LORE generated per Loot bag&#65292;', 
            'each day for 100 years&#65292;', 
            'and nevermore until the end of time', 
            '', 
            'We encourage developers to make LORE deflationary through use', 
            '', 
            'You may not represent yourself as the creator of LORE or the LORE Foundation&#65292;', 
            'but otherwise feel free to use LORE in any way you want'];
    }

    function claimForToken (uint256 LootId) external nonReentrant {
        require (msg.sender==Loot.ownerOf(LootId),"Attempted to claim for Loot you don't own");
        releaseGrant(LootId);
    }

    function claimForMultiple (uint256 [] memory LootIds) external nonReentrant {
        for (uint256 i=0;i<LootIds.length;i++)
        {
            require (msg.sender==Loot.ownerOf(LootIds[i]),"Attempted to claim for Loot you don't own");
        }
        for (uint256 i=0;i<LootIds.length;i++)
        {
            releaseGrant(LootIds[i]);
        }
    }

    function claimForAllTokensOfOwner () external nonReentrant {
        uint256 balance = Loot.balanceOf(msg.sender);
        require (balance>0,"You don't own any Loot");
        for (uint256 i=0;i<balance;i++)
        {
            releaseGrant(Loot.tokenOfOwnerByIndex(msg.sender,i));
        }
    }
    
    function releaseGrant (uint256 tokenId) private {
        uint256 grantForToken = unclaimedGrantBalanceByToken(tokenId);
        cumulativeClaimedByTokenId[tokenId] += grantForToken;
        _mint(msg.sender,grantForToken);
    }

    function unclaimedGrantBalanceByToken (uint256 tokenId) public view returns (uint256) {   
        return cumulativeEntitlementPerToken() - cumulativeClaimedByTokenId[tokenId];
    }

    function cumulativeEntitlementPerToken () public view returns (uint256){
        uint256 GrantTime = min (block.timestamp, END) - START;
        return GrantTime * RATE / DISTRIBUTION_PERIOD;
    }

    function unclaimedGrantBalanceByAddress (address owner) public view returns (uint256) {
        uint256 unclaimed;  
        for (uint256 i=0;i<Loot.balanceOf(owner);i++)
            {
                unclaimed += unclaimedGrantBalanceByToken(Loot.tokenOfOwnerByIndex(owner,i));
            }      
        return unclaimed;
    }

    function timeRemainingUntilEndOfIssuance () public view returns (uint256) {
        return END - block.timestamp;
    }

    function timeSinceStart () public view returns (uint256) {
        return block.timestamp - START;
    }

    function whitepaper (uint anyNumber) external view returns (string [] memory) {
        return _whitepaper;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}
