contract KoiBoiERC20 is Ownable {

    uint256 private constant BOI_ETH_PRICE = 50000000000000000; //0.05 ETH
    uint256 public constant BOI_CAVIAR_PRICE = 50000000000000000;
    uint16 private constant MAX_MINT = 20;

    address public BOI_ADDRESS = 0xC12392D73dBC98233E405577E4A564603f5c0077;
    address public CAVIAR_ADDRESS = 0xFDF371B968BfA4942559ADddCcB2041bC2e26e87;

    IKOIBOI private _boiNFTCONTRACT = IKOIBOI(BOI_ADDRESS);
    IERC20Metadata private _CAVIARCONTRACT = IERC20Metadata(CAVIAR_ADDRESS);

    bool private saleOn = false;


    /**
     * @dev Token Name.
     */
    function name() external pure returns (string memory) {
        return "KoiBoiERC20";
    }

    /**
     * @dev Token Symbol.
     */
    function symbol() external pure returns (string memory) {
        return "KOIBOIERC20";
    }

    function mint(uint256 amount) external {
        require(saleOn, "SALE NOT STARTED");
        require(amount <= MAX_MINT, "CAN NOT MINT MORE THAN 20 NFTS");
        require(_CAVIARCONTRACT.balanceOf(msg.sender) >= amount*BOI_CAVIAR_PRICE, "YOU HAVE LOW CAVIAR BALANCE");
        _CAVIARCONTRACT.transferFrom(msg.sender, address(this), amount*BOI_CAVIAR_PRICE);

        address[] memory to = new address[](amount);
        for(uint256 i=0; i<amount; i++)
        {
            to[i] = msg.sender;
        }
        _boiNFTCONTRACT.multi_mint_to_others{value:to.length*BOI_ETH_PRICE}(to);
    }

    receive() external payable {
    }

    function flipSale() external onlyOwner {
        saleOn = !saleOn;
    }

    function isSaleOn() external view returns(bool) {
        return saleOn;
    }

    function changeCAVIARAddress(address caviarAddress) external onlyOwner {
        CAVIAR_ADDRESS = caviarAddress;
        _CAVIARCONTRACT = IERC20Metadata(caviarAddress);
    }

    function changeBOIAddress(address boiAddress) external onlyOwner {
        BOI_ADDRESS = boiAddress;
        _boiNFTCONTRACT = IKOIBOI(boiAddress);
    }

    function claimBOIOwnership(address boiNewOwner) external onlyOwner {
        _boiNFTCONTRACT.transferOwnership(boiNewOwner);
    }

    function withdrawETH() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function withdrawTokens(address tokenAddress, uint256 amount) external onlyOwner {
        require(IERC20(tokenAddress).balanceOf(address(this)) >= amount, "NOT ENOUGH TOKEN BALANCE");
        IERC20(tokenAddress).transfer(msg.sender, amount);
    }

    function withdrawNFTs(address nftAddress, uint256[] calldata ids) external onlyOwner {
        for(uint256 i=0; i<ids.length; i++) {
            IERC721(nftAddress).safeTransferFrom(address(this), msg.sender, ids[i]);
        }
    }

}"
