    ERC1967Proxy(_vesting, _data)
  {
    assert(
      _FACTORY_SLOT ==
        bytes32(uint256(keccak256("co.coinvise.vesting.factory")) - 1)
    );
    require(IVestingFactory(msg.sender).vesting() != address(0));
    _setFactory(msg.sender);
  }

