interface IStakingPool {
  function balanceOf(address _owner) external view returns(uint256);
  
  function mint(address[] calldata addresses, uint256[] calldata points) external;

  function burn(address owner, uint256 amount) external;
}

contract MainnetCoinBridge is AccessControl {
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

  using SafeMath for uint256;

  event RainbowsDeposited(address indexed spender, uint256 amount, uint256 requestId);
  event UnicornsDeposited(address indexed spender, uint256 amount, uint256 requestId);
  event RainbowsWithdrawn(address indexed owner, uint256 amount, uint256 requestId);
  event UnicornsWithdrawn(address indexed owner, uint256 amount, uint256 requestId);
  event EthWithdrawn(uint256 amount);
  event FeeSet(uint256 fee);

  uint256 public requestId;
  uint256 public fee;

  address private stakingPool;
  address private lpStakingPool;
  address private lpV3StakingPool;

  constructor(address _stakingPool, address _lpStakingPool, address _lpV3StakingPool) {
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    stakingPool = _stakingPool;
    lpStakingPool = _lpStakingPool;
    lpV3StakingPool = _lpV3StakingPool;
  }

  modifier onlyMinter() {
    require(hasRole(MINTER_ROLE, _msgSender()), "MainnetCoinBridge: caller is not a minter");
    _;
  }

  modifier onlyOwner() {
    require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "MainnetCoinBridge: caller is not an owner");
    _;
  }

  function setFee(uint256 _fee) external onlyOwner {
    fee = _fee;
    emit FeeSet(_fee);
  }

  function depositRainbows(uint256 _amount) external payable {
    require(msg.value >= fee, "MainnetCoinBridge: not enough eth");
    require(IStakingPool(stakingPool).balanceOf(_msgSender()) >= _amount, "MainnetCoinBridge: not enough rainbows");

    IStakingPool(stakingPool).burn(_msgSender(), _amount);

    uint256 refund = msg.value - fee;
    if (refund > 0) {
      (bool refundSuccess, ) = _msgSender().call{ value: refund }("");
      require(refundSuccess, "MainnetCoinBridge: refund transfer failed");
    }

    requestId++;
    emit RainbowsDeposited(_msgSender(), _amount, requestId);
  }

  function depositUnicorns(uint256 _amount) external payable {
    require(msg.value >= fee, "MainnetCoinBridge: not enough eth");
    
    uint256 oldPoolBalance = IStakingPool(lpStakingPool).balanceOf(_msgSender());
    int256 newPoolLeftAmount = int256(_amount) - int256(oldPoolBalance);
    if (newPoolLeftAmount > 0) {
      require(IStakingPool(lpV3StakingPool).balanceOf(_msgSender()) >= uint256(newPoolLeftAmount), "MainnetCoinBridge: not enough unicorns");
      if (oldPoolBalance > 0) IStakingPool(lpStakingPool).burn(_msgSender(), oldPoolBalance);
      IStakingPool(lpV3StakingPool).burn(_msgSender(), uint256(newPoolLeftAmount));
    } else {
      IStakingPool(lpStakingPool).burn(_msgSender(), _amount);
    }

    uint256 refund = msg.value - fee;
    if (refund > 0) {
      (bool refundSuccess, ) = _msgSender().call{ value: refund }("");
      require(refundSuccess, "MainnetCoinBridge: refund transfer failed");
    }

    requestId++;
    emit UnicornsDeposited(_msgSender(), _amount, requestId);
  }

  function withdrawRainbows(address[] calldata _owners, uint256[] calldata _amounts, uint256[] calldata _requestsIds) 
    external onlyMinter {
      require(_owners.length == _amounts.length && _owners.length == _requestsIds.length, "MainnetCoinBridge: Arrays length not equal");
      IStakingPool(stakingPool).mint(_owners, _amounts);
      for (uint256 i; i < _owners.length; i++) {
        emit RainbowsWithdrawn(_owners[i], _amounts[i], _requestsIds[i]);
      }
  }

  function withdrawUnicorns(address[] calldata _owners, uint256[] calldata _amounts, uint256[] calldata _requestsIds) 
    external onlyMinter {
      require(_owners.length == _amounts.length && _owners.length == _requestsIds.length, "MainnetCoinBridge: Arrays length not equal");
      IStakingPool(lpV3StakingPool).mint(_owners, _amounts);
      for (uint256 i; i < _owners.length; i++) {
        emit UnicornsWithdrawn(_owners[i], _amounts[i], _requestsIds[i]);
      }
  }

  function withdrawEth(uint256 _amount)
    external onlyOwner {
      require(_amount <= address(this).balance, "MainnetCoinBridge: not enough balance");
      (bool success, ) = _msgSender().call{ value: _amount }("");
      require(success, "MainnetCoinBridge: transfer failed");
      emit EthWithdrawn(_amount);
  }
}
