// File: contracts/FeeCollectable.sol



pragma solidity ^0.8.0;




contract FeeCollectable is Ownable, IFeeCollectable{
    address public feeTo;

    /// @inheritdoc IFeeCollectable
    function setFeeTo(address _feeTo) onlyOwner external override{
        feeTo = _feeTo;
        emit FeeToChangedEvt(_feeTo);
    }

    /// @inheritdoc IFeeCollectable
    function refund() external override{
        uint amount = address(this).balance;
        TransferHelper.safeTransferETH(feeTo, amount);
        emit FeeWithdrawedEvt(feeTo, amount);
    }

}
// File: contracts/HistoryV1Market.sol



pragma solidity ^0.8.0;







contract HistoryV1Market is IHistoryV1Market, FeeCollectable{
    using SafeCast for uint256;

    struct SaleInfo{
        uint256 tokenId;
        address owner; // 160
        uint96  price; 
    }
    mapping(bytes32 => SaleInfo) private saleInfo;

    /// @inheritdoc IHistoryV1Market
    function sellNft(address token, uint256 tokenId, uint256 price) external override{
        require(price >= 0, "Invalid price");
        require(IERC721(token).ownerOf(tokenId) == msg.sender, "Not owner");
        require(
            IERC721(token).getApproved(tokenId) == address(this) ||
            IERC721(token).isApprovedForAll(msg.sender, address(this)),
            "Approve first."
        );
        SaleInfo storage info = saleInfo[keccak256(abi.encodePacked(token,tokenId))];
        (info.tokenId, info.owner, info.price) = (tokenId, msg.sender, price.toUint96());
        emit SaleInfoChangedEvt(token, tokenId, price);
    }

    /// @inheritdoc IHistoryV1Market
    function buyNft(address token, uint256 tokenId, address to) external payable override {
        SaleInfo storage info = saleInfo[keccak256(abi.encodePacked(token,tokenId))];
        SaleInfo memory _info = info;
        require(msg.value >= _info.price, "Value not enough.");
        require(_info.price > 0, "Not on sale.");
        // transfer nft
        TransferHelper.safeTransferNFTFrom(token, _info.owner, to, tokenId);
        // pay eth
        TransferHelper.safeTransferETH(_info.owner, _info.price);
        // update value
        (info.owner, info.price) = (to ,0);
        emit SaleInfoChangedEvt(token, tokenId, 0);
        emit NFTSoldEvt(token, tokenId, _info.price);
    }

    /// @inheritdoc IHistoryV1Market
    function tokenSaleInfo(
        address token, 
        uint256 tokenId
    ) external override view returns(bool onSale, uint256 price){
        SaleInfo memory info = saleInfo[keccak256(abi.encodePacked(token,tokenId))];
        if(
            // Check still on sale
            info.price > 0 &&
            // check owner is still correct
            IERC721(token).ownerOf(tokenId) == info.owner && 
            (
                // Check approve
                IERC721(token).getApproved(tokenId) == address(this) ||
                IERC721(token).isApprovedForAll(info.owner, address(this))
            )
        ){
            return (true, info.price);
        }
        else{
            return (false, 0);
        }
    }

}
